'use client';

import { updateQuotePricing } from '@/lib/actions/admin-requests';
import { Quote } from '@/services';
import { format } from 'date-fns';
import { ArrowLeft, Send, XCircle, AlertCircle, Save, Lock, CheckCircle } from 'lucide-react';
import Link from 'next/link';
import { useState, useTransition } from 'react';
import { useRouter } from 'next/navigation';

interface RequestDetailViewProps {
    request: Quote;
    lng: string;
}

export default function RequestDetailView({ request, lng }: RequestDetailViewProps) {
    const router = useRouter();
    const [isPending, startTransition] = useTransition();
    const [price, setPrice] = useState<string>(request.estimatedPrice?.toString() || '');
    const [file, setFile] = useState<File | null>(null);
    const [error, setError] = useState<string | null>(null);

    const items = JSON.parse(request.itemsJson || '[]');

    // Calculate if within 15-minute grace period
    const GRACE_PERIOD_MINUTES = 15;
    const isWithinGracePeriod = () => {
        if (!request.quotedAt) return false;
        const quotedTime = new Date(request.quotedAt).getTime();
        const now = Date.now();
        const minutesSinceQuoted = (now - quotedTime) / (1000 * 60);
        return minutesSinceQuoted < GRACE_PERIOD_MINUTES;
    };

    // Lock form if:
    // - Explicitly locked OR
    // - Status is quoted/confirmed AND grace period has expired
    const isQuotedOrConfirmed = request.status === 'quoted' || request.status === 'confirmed';
    const isLocked = request.isLocked || (isQuotedOrConfirmed && !isWithinGracePeriod());

    // Calculate remaining time for display
    const getRemainingMinutes = () => {
        if (!request.quotedAt || !isQuotedOrConfirmed) return 0;
        const quotedTime = new Date(request.quotedAt).getTime();
        const now = Date.now();
        const minutesSinceQuoted = (now - quotedTime) / (1000 * 60);
        return Math.max(0, Math.ceil(GRACE_PERIOD_MINUTES - minutesSinceQuoted));
    };
    const remainingMinutes = getRemainingMinutes();

    const handleSendQuote = async () => {
        if (!price || isNaN(Number(price))) {
            setError('Please enter a valid price');
            return;
        }
        if (!file && !isLocked) {
            setError('Please upload a quote PDF');
            return;
        }

        startTransition(async () => {
            const formData = new FormData();
            formData.append('id', request.id);
            formData.append('price', price);
            if (file) formData.append('file', file);

            const result = await updateQuotePricing(formData);
            if (result.success) {
                router.refresh();
                router.push(`/${lng}/admin/requests`); // Redirect to inbox
            } else {
                setError(result.error || 'Failed to send quote');
            }
        });
    };

    return (
        <div className="space-y-6">
            {/* Header */}
            <div className="flex items-center justify-between">
                <Link
                    href={`/${lng}/admin/requests`}
                    className="flex items-center text-sm text-zinc-400 hover:text-white transition-colors"
                >
                    <ArrowLeft className="w-4 h-4 mr-1" />
                    Back to Inbox
                </Link>
                <div className="flex items-center gap-2">
                    {isLocked && (
                        <span className="px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-500/10 text-red-500 ring-1 ring-inset ring-red-500/20 flex items-center gap-1">
                            <Lock className="w-3 h-3" /> LOCKED
                        </span>
                    )}
                    {/* Grace period indicator */}
                    {!isLocked && isQuotedOrConfirmed && remainingMinutes > 0 && (
                        <span className="px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-500/10 text-orange-500 ring-1 ring-inset ring-orange-500/20 flex items-center gap-1">
                            ΓÅ▒∩╕Å {remainingMinutes}min to edit
                        </span>
                    )}
                    <span className={`px-2.5 py-0.5 rounded-full text-xs font-medium ring-1 ring-inset ${request.status === 'pending' ? 'bg-yellow-500/10 text-yellow-500 ring-yellow-500/20' :
                        request.status === 'quoted' ? 'bg-blue-500/10 text-blue-500 ring-blue-500/20' :
                            'bg-green-500/10 text-green-500 ring-green-500/20'
                        }`}>
                        {request.status.toUpperCase()}
                    </span>
                </div>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                {/* Main Content: Details & Items */}
                <div className="lg:col-span-2 space-y-6">

                    {/* Client Card */}
                    <div className="bg-zinc-900 border border-zinc-800 rounded-lg p-6">
                        <h3 className="text-lg font-semibold text-white mb-4">Client Details</h3>
                        <div className="grid grid-cols-2 gap-4 text-sm">
                            <div>
                                <p className="text-zinc-500">Name</p>
                                <p className="text-white font-medium">{request.clientName}</p>
                            </div>
                            <div>
                                <p className="text-zinc-500">Email</p>
                                <p className="text-white font-medium">{request.clientEmail}</p>
                            </div>
                            <div>
                                <p className="text-zinc-500">Company</p>
                                <p className="text-white font-medium">{request.clientCompany || '-'}</p>
                            </div>
                            <div>
                                <p className="text-zinc-500">Dates</p>
                                <p className="text-white font-medium">
                                    {format(new Date(request.rentalStartDate), 'MMM d, yyyy')} - {format(new Date(request.rentalEndDate), 'MMM d, yyyy')}
                                </p>
                            </div>
                        </div>
                        {request.projectDescription && (
                            <div className="mt-4 pt-4 border-t border-zinc-800">
                                <p className="text-zinc-500 mb-1">Project Description</p>
                                <p className="text-zinc-300">{request.projectDescription}</p>
                            </div>
                        )}
                    </div>

                    {/* Items Table */}
                    <div className="bg-zinc-900 border border-zinc-800 rounded-lg overflow-hidden">
                        <div className="px-6 py-4 border-b border-zinc-800 bg-zinc-900/50">
                            <h3 className="text-lg font-semibold text-white">Requested Items</h3>
                        </div>
                        <table className="min-w-full divide-y divide-zinc-800">
                            <thead className="bg-zinc-950">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-zinc-400 uppercase tracking-wider">Item</th>
                                    <th className="px-6 py-3 text-right text-xs font-medium text-zinc-400 uppercase tracking-wider">Qty</th>
                                </tr>
                            </thead>
                            <tbody className="divide-y divide-zinc-800 bg-zinc-900">
                                {items.map((item: any, idx: number) => (
                                    <tr key={idx}>
                                        <td className="px-6 py-4">
                                            <div className="text-sm font-medium text-white">{item.name}</div>
                                            {item.kitSelections && Object.keys(item.kitSelections).length > 0 && (
                                                <div className="mt-1 flex flex-wrap gap-1">
                                                    {Object.entries(item.kitSelections).map(([key, val]) => (
                                                        <span key={key} className="inline-flex items-center px-1.5 py-0.5 rounded text-xs font-medium bg-zinc-800 text-zinc-400 border border-zinc-700">
                                                            {val as string}
                                                        </span>
                                                    ))}
                                                </div>
                                            )}
                                        </td>
                                        <td className="px-6 py-4 text-right text-sm text-white font-mono">
                                            {item.quantity}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>

                {/* Action Panel */}
                <div className="space-y-6">
                    <div className="bg-zinc-900 border border-zinc-800 rounded-lg p-6 sticky top-6">
                        <h3 className="text-lg font-semibold text-white mb-4">Pricing & Actions</h3>

                        <div className="space-y-4">
                            <div>
                                <label htmlFor="price" className="block text-sm font-medium text-zinc-400 mb-1">
                                    Total Estimated Price ($)
                                </label>
                                <div className="relative">
                                    <span className="absolute left-3 top-1/2 -translate-y-1/2 text-zinc-500">$</span>
                                    <input
                                        type="number"
                                        id="price"
                                        value={price}
                                        onChange={(e) => {
                                            setPrice(e.target.value);
                                            setError(null);
                                        }}
                                        disabled={isLocked}
                                        placeholder="0.00"
                                        className="block w-full pl-7 pr-3 py-2 bg-zinc-950 border border-zinc-800 rounded-md text-white placeholder-zinc-600 focus:outline-none focus:ring-2 focus:ring-red-500/50 focus:border-red-500 disabled:opacity-50 disabled:cursor-not-allowed"
                                    />
                                </div>
                            </div>

                            {/* File Upload */}
                            <div>
                                <label htmlFor="quote-file" className="block text-sm font-medium text-zinc-400 mb-1">
                                    Upload Official Quote (PDF)
                                </label>
                                <input
                                    type="file"
                                    id="quote-file"
                                    accept=".pdf"
                                    onChange={(e) => {
                                        if (e.target.files && e.target.files[0]) {
                                            setFile(e.target.files[0]);
                                            setError(null);
                                        }
                                    }}
                                    disabled={isLocked}
                                    className="block w-full text-sm text-zinc-400 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-semibold file:bg-zinc-800 file:text-zinc-300 hover:file:bg-zinc-700 disabled:opacity-50 disabled:cursor-not-allowed"
                                />
                                {isLocked && request.pdfFileName && (
                                    <p className="mt-2 text-xs text-green-500 flex items-center gap-1">
                                        <CheckCircle className="w-3 h-3" /> Uploaded: {request.pdfFileName}
                                    </p>
                                )}
                            </div>

                            {error && (
                                <div className="p-3 rounded-md bg-red-500/10 border border-red-500/20 text-red-400 text-sm flex items-start gap-2">
                                    <AlertCircle className="w-4 h-4 mt-0.5 flex-shrink-0" />
                                    <span>{error}</span>
                                </div>
                            )}

                            <div className="pt-4 border-t border-zinc-800 space-y-3">
                                <button
                                    onClick={handleSendQuote}
                                    disabled={isPending || isLocked}
                                    className="w-full flex items-center justify-center gap-2 px-4 py-2 rounded-md bg-red-600 text-white font-medium hover:bg-red-500 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                                >
                                    {isPending ? (
                                        <span className="animate-pulse">Uploading...</span>
                                    ) : isLocked ? (
                                        <>
                                            <CheckCircle className="w-4 h-4" />
                                            Quote Sent & Locked
                                        </>
                                    ) : (
                                        <>
                                            <Send className="w-4 h-4" />
                                            Send Quote
                                        </>
                                    )}
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import { Quote } from '@/services';
import Link from 'next/link';
import { Eye, Clock, CheckCircle, XCircle, AlertCircle } from 'lucide-react';
import { format } from 'date-fns';
import {
    Table,
    TableHeader,
    TableBody,
    TableColumn,
    TableRow,
    TableCell,
    User,
    Chip,
    Tooltip,
    Button
} from "@heroui/react";

interface RequestsTableProps {
    requests: Quote[];
    lng: string;
}

const statusColorMap: Record<string, "warning" | "primary" | "success" | "danger" | "default"> = {
    pending: "warning",
    reviewing: "primary",
    confirmed: "success",
    rejected: "danger",
};

const columns = [
    { name: "REFERENCE", uid: "id" },
    { name: "CLIENT", uid: "client" },
    { name: "DATES", uid: "dates" },
    { name: "ITEMS", uid: "items" },
    { name: "STATUS", uid: "status" },
    { name: "ACTIONS", uid: "actions" },
];

export default function RequestsTable({ requests, lng }: RequestsTableProps) {

    // Render cell content based on column
    const renderCell = (request: Quote, columnKey: React.Key) => {
        const cellValue = request[columnKey as keyof Quote];
        const statusColor = statusColorMap[request.status] || "default";

        switch (columnKey) {
            case "id":
                return (
                    <div className="flex flex-col">
                        <p className="text-bold text-sm capitalize text-white">
                            {(request as any).confirmationNumber || request.id.slice(0, 8).toUpperCase()}
                        </p>
                    </div>
                );
            case "client":
                return (
                    <User
                        avatarProps={{ radius: "lg", src: "" /* Add avatar url if available */ }}
                        description={request.clientEmail}
                        name={request.clientName}
                    >
                        {request.clientEmail}
                    </User>
                );
            case "dates":
                const days = Math.ceil((new Date(request.rentalEndDate).getTime() - new Date(request.rentalStartDate).getTime()) / (1000 * 60 * 60 * 24));
                return (
                    <div className="flex flex-col">
                        <p className="text-bold text-sm text-white">
                            {format(new Date(request.rentalStartDate), 'MMM d')} - {format(new Date(request.rentalEndDate), 'MMM d')}
                        </p>
                        <p className="text-tiny text-default-400 capitalize">{days} Days</p>
                    </div>
                );
            case "items":
                // Safely parse if string, though Service Layer guarantees string. 
                // Double safety or assume string from Service logic.
                let count = 0;
                try {
                    const parsed = JSON.parse(request.itemsJson);
                    count = Array.isArray(parsed) ? parsed.length : 0;
                } catch (e) { count = 0; }

                return (
                    <div className="flex flex-col">
                        <p className="text-bold text-sm text-white">{count} item{count !== 1 ? 's' : ''}</p>
                    </div>
                );
            case "status":
                return (
                    <Chip className="capitalize" color={statusColor} size="sm" variant="flat">
                        {request.status}
                    </Chip>
                );
            case "actions":
                return (
                    <div className="relative flex items-center gap-2">
                        <Tooltip content="Review Request">
                            <Button
                                isIconOnly
                                as={Link}
                                href={`/${lng}/admin/requests/${request.id}`}
                                size="sm"
                                variant="light"
                                className="text-default-400 cursor-pointer active:opacity-50"
                            >
                                <Eye className="w-5 h-5" />
                            </Button>
                        </Tooltip>
                    </div>
                );
            default:
                return cellValue as React.ReactNode;
        }
    };

    if (requests.length === 0) {
        return (
            <div className="text-center py-20 bg-default-50 rounded-lg border border-default-100">
                <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-default-100 mb-4">
                    <AlertCircle className="w-8 h-8 text-default-500" />
                </div>
                <h3 className="text-lg font-medium text-white">No requests found</h3>
                <p className="mt-1 text-sm text-default-400">Your inbox is completely clear.</p>
            </div>
        );
    }

    return (
        <Table aria-label="Rental requests table">
            <TableHeader columns={columns}>
                {(column) => (
                    <TableColumn key={column.uid} align={column.uid === "actions" ? "center" : "start"}>
                        {column.name}
                    </TableColumn>
                )}
            </TableHeader>
            <TableBody items={requests}>
                {(item) => (
                    <TableRow key={item.id}>
                        {(columnKey) => <TableCell>{renderCell(item, columnKey)}</TableCell>}
                    </TableRow>
                )}
            </TableBody>
        </Table>
    );
}

import { cn } from "@/lib/utils";

interface AvatarProps {
  src: string;
  alt?: string;
  size?: 'xs' | 'sm' | 'md' | 'lg' | 'xl' | '2xl';
  shape?: 'circle' | 'rounded';
  className?: string;
}

const sizeClasses = {
  xs: "size-6",
  sm: "size-8",
  md: "size-10",
  lg: "size-12",
  xl: "size-14",
  '2xl': "size-16",
};

export default function Avatar({ 
    src, 
    alt = "", 
    size = 'md', 
    shape = 'circle', 
    className 
}: AvatarProps) {
  return (
    <img
      alt={alt}
      src={src}
      className={cn(
          "inline-block object-cover",
          sizeClasses[size],
          shape === 'circle' ? "rounded-full" : "rounded-md",
          className
      )}
    />
  )
}

import { cn } from "@/lib/utils";

interface ButtonGroupItem {
  id: string | number;
  label: string;
}

interface ButtonGroupProps {
  items: ButtonGroupItem[];
  value: string | number;
  onChange: (value: any) => void;
  className?: string;
}

export default function ButtonGroup({ items, value, onChange, className }: ButtonGroupProps) {
  return (
    <span className={cn("isolate inline-flex rounded-md shadow-sm", className)}>
      {items.map((item, idx) => (
        <button
          key={item.id}
          type="button"
          onClick={() => onChange(item.id)}
          className={cn(
            "relative inline-flex items-center px-3 py-2 text-sm font-semibold ring-1 ring-inset focus:z-10",
            // Borders based on position
            idx === 0 ? "rounded-l-md" : "-ml-px",
            idx === items.length - 1 ? "rounded-r-md" : "",
            // Active state
            item.id === value
              ? "bg-indigo-600 text-white ring-indigo-600 hover:bg-indigo-500"
              : "bg-white text-gray-900 ring-gray-300 hover:bg-gray-50 dark:bg-gray-800 dark:text-gray-200 dark:ring-gray-700 dark:hover:bg-gray-700"
          )}
        >
          {item.label}
        </button>
      ))}
    </span>
  )
}

"use client";

import { Menu, MenuButton, MenuItem, MenuItems } from '@headlessui/react'
import { ChevronDown } from 'lucide-react'
import Link from 'next/link';
import { cn } from "@/lib/utils";

interface DropdownItem {
  label: string;
  href?: string;
  onClick?: () => void;
  icon?: React.ElementType;
}

interface DropdownProps {
  label?: string | React.ReactNode;
  items: DropdownItem[];
  className?: string; // wrapper class
}

export default function Dropdown({ label = "Options", items, className }: DropdownProps) {
  return (
    <Menu as="div" className={cn("relative inline-block text-left", className)}>
      <div>
        <MenuButton className="inline-flex w-full justify-center gap-x-1.5 rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50 dark:bg-gray-800 dark:text-gray-200 dark:ring-gray-700 dark:hover:bg-gray-700">
          {label}
          <ChevronDown className="-mr-1 h-5 w-5 text-gray-400" aria-hidden="true" />
        </MenuButton>
      </div>

      <MenuItems
        transition
        className="absolute right-0 z-10 mt-2 w-56 origin-top-right rounded-md bg-white shadow-lg ring-1 ring-black ring-opacity-5 transition focus:outline-none data-[closed]:scale-95 data-[closed]:transform data-[closed]:opacity-0 data-[enter]:duration-100 data-[leave]:duration-75 data-[enter]:ease-out data-[leave]:ease-in dark:bg-gray-800 dark:ring-gray-700"
      >
        <div className="py-1">
          {items.map((item, index) => (
             <MenuItem key={index}>
                {({ focus }) => {
                     const Element = item.href ? Link : 'button';
                     const elementProps = item.href ? { href: item.href } : { onClick: item.onClick, type: 'button' as const };
                     const content = (
                        <>
                             {item.icon && <item.icon className="mr-3 h-5 w-5 text-gray-400 group-hover:text-gray-500" aria-hidden="true" />}
                             {item.label}
                        </>
                     );

                     // Type assertion workaround for dynamic component props in TS
                     return (
                         // @ts-ignore
                         <Element
                            {...elementProps}
                             className={cn(
                                 focus ? 'bg-gray-100 text-gray-900 dark:bg-gray-700 dark:text-white' : 'text-gray-700 dark:text-gray-300',
                                 'group flex w-full items-center px-4 py-2 text-sm'
                             )}
                         >
                             {content}
                         </Element>
                     )
                }}
             </MenuItem>
          ))}
        </div>
      </MenuItems>
    </Menu>
  )
}

