"use client";

import {
  Combobox,
  ComboboxInput,
  ComboboxOption,
  ComboboxOptions,
  Dialog,
  DialogPanel,
  DialogBackdrop,
  Transition,
  TransitionChild,
} from '@headlessui/react';
import { Fragment, useState, useEffect } from 'react';
import { Search, FolderPlus, FilePlus, Hash, Tag, AlertCircle } from 'lucide-react';
import { cn } from "@/lib/utils";
import Link from 'next/link';

interface CommandItem {
    id: string | number;
    name: string;
    url?: string;
    shortcut?: string;
    icon?: React.ElementType;
    onClick?: () => void;
    group?: string;
}

interface CommandPaletteProps {
  open: boolean;
  setOpen: (open: boolean) => void;
  navigation: CommandItem[];
  placeholder?: string;
}

export default function CommandPalette({ open, setOpen, navigation, placeholder = "Search..." }: CommandPaletteProps) {
  const [query, setQuery] = useState('');

  // Reset query when closed
  useEffect(() => {
    if (!open) {
        setQuery('');
    }
  }, [open]);

  const filteredItems =
    query === ''
      ? []
      : navigation.filter((item) => {
          return item.name.toLowerCase().includes(query.toLowerCase())
        });
  
  // Group logic could be added here if navigation has 'group' property

  return (
    <Transition show={open} as={Fragment} afterLeave={() => setQuery('')} appear>
      <Dialog as="div" className="relative z-50" onClose={setOpen}>
        <TransitionChild
          as={Fragment}
          enter="ease-out duration-300"
          enterFrom="opacity-0"
          enterTo="opacity-100"
          leave="ease-in duration-200"
          leaveFrom="opacity-100"
          leaveTo="opacity-0"
        >
          <DialogBackdrop className="fixed inset-0 bg-gray-500/25 transition-opacity dark:bg-black/50 backdrop-blur-sm" />
        </TransitionChild>

        <div className="fixed inset-0 z-10 w-screen overflow-y-auto p-4 sm:p-6 md:p-20">
          <TransitionChild
            as={Fragment}
            enter="ease-out duration-300"
            enterFrom="opacity-0 scale-95"
            enterTo="opacity-100 scale-100"
            leave="ease-in duration-200"
            leaveFrom="opacity-100 scale-100"
            leaveTo="opacity-0 scale-95"
          >
            <DialogPanel className="mx-auto max-w-2xl transform divide-y divide-gray-100 overflow-hidden rounded-xl bg-white shadow-2xl ring-1 ring-black/5 transition-all dark:bg-gray-900 dark:divide-gray-800 dark:ring-gray-700">
              <Combobox
                onChange={(item: CommandItem) => {
                  if (item?.url) {
                    window.location.href = item.url; // Or Next.js router push
                  } else if (item?.onClick) {
                    item.onClick();
                  }
                  setOpen(false);
                }}
              >
                <div className="relative">
                  <Search
                    className="pointer-events-none absolute left-4 top-3.5 h-5 w-5 text-gray-400 dark:text-gray-500"
                    aria-hidden="true"
                  />
                  <ComboboxInput
                    className="h-12 w-full border-0 bg-transparent pl-11 pr-4 text-gray-900 placeholder:text-gray-400 focus:ring-0 sm:text-sm dark:text-white dark:placeholder:text-gray-500"
                    placeholder={placeholder}
                    onChange={(event) => setQuery(event.target.value)}
                    displayValue={(item: CommandItem) => item?.name}
                  />
                </div>

                {(query === '' || filteredItems.length > 0) && (
                  <ComboboxOptions static className="max-h-80 scroll-py-2 divide-y divide-gray-100 overflow-y-auto dark:divide-gray-800">
                    <li className="p-2">
                        {query === '' && (
                            <h2 className="mb-2 mt-4 px-3 text-xs font-semibold text-gray-500 dark:text-gray-400">
                                Suggested
                            </h2>
                        )}
                         {/* Show filtered if searching, or recent/all if not (simplified logic for now) */}
                         <ul className="text-sm text-gray-700 dark:text-gray-200">
                             {(query === '' ? navigation.slice(0, 5) : filteredItems).map((item) => (
                                <ComboboxOption
                                    key={item.id}
                                    value={item}
                                    className={({ focus }) =>
                                    cn(
                                        "group flex cursor-default select-none items-center rounded-md px-3 py-2",
                                        focus ? "bg-indigo-600 text-white" : "text-gray-900 dark:text-gray-200"
                                    )
                                    }
                                >
                                    {({ focus }) => (
                                    <>
                                        {item.icon ? (
                                            <item.icon
                                                className={cn(
                                                    "h-6 w-6 flex-none",
                                                    focus ? "text-white" : "text-gray-400 dark:text-gray-500"
                                                )}
                                                aria-hidden="true"
                                            />
                                        ) : (
                                            <FolderPlus className={cn("h-6 w-6 flex-none", focus ? "text-white" : "text-gray-400")} />
                                        )}
                                        <span className="ml-3 flex-auto truncate">{item.name}</span>
                                        {item.shortcut && (
                                            <span className={cn(
                                                "ml-3 flex-none text-xs font-semibold",
                                                focus ? "text-indigo-100" : "text-gray-400"
                                            )}>
                                                <kbd className="font-sans">Γîÿ</kbd>
                                                <kbd className="font-sans">{item.shortcut}</kbd>
                                            </span>
                                        )}
                                    </>
                                    )}
                                </ComboboxOption>
                             ))}
                         </ul>
                    </li>
                  </ComboboxOptions>
                )}

                {query !== '' && filteredItems.length === 0 && (
                  <div className="px-6 py-14 text-center sm:px-14">
                    <AlertCircle className="mx-auto h-6 w-6 text-gray-400 dark:text-gray-500" aria-hidden="true" />
                    <p className="mt-4 text-sm text-gray-900 dark:text-gray-200">
                      We couldn't find any items showing "{query}".
                    </p>
                  </div>
                )}
              </Combobox>
            </DialogPanel>
          </TransitionChild>
        </div>
      </Dialog>
    </Transition>
  )
}

import { ChevronLeft, ChevronRight } from 'lucide-react';
import { cn } from "@/lib/utils";
import Link from 'next/link';

interface PaginationProps {
  currentPage: number;
  totalPages: number;
  onPageChange?: (page: number) => void;
  className?: string; // wrapper style
}

export default function Pagination({ currentPage, totalPages, onPageChange, className }: PaginationProps) {
  // Simple range generation
  // In a real app, you'd want complex logic for "..." truncation
  const pages = Array.from({ length: totalPages }, (_, i) => i + 1);

  return (
    <nav className={cn("flex items-center justify-between border-t border-gray-200 px-4 sm:px-0", className)}>
      <div className="-mt-px flex w-0 flex-1">
        <button
          onClick={() => onPageChange?.(Math.max(1, currentPage - 1))}
          disabled={currentPage === 1}
          className="inline-flex items-center border-t-2 border-transparent pr-1 pt-4 text-sm font-medium text-gray-500 hover:border-gray-300 hover:text-gray-700 disabled:opacity-50 disabled:hover:border-transparent disabled:hover:text-gray-500 dark:text-gray-400 dark:hover:text-gray-300"
        >
          <ChevronLeft className="mr-3 h-5 w-5 text-gray-400" aria-hidden="true" />
          Previous
        </button>
      </div>
      <div className="hidden md:-mt-px md:flex">
        {pages.map((page) => (
          <button
             key={page}
             onClick={() => onPageChange?.(page)}
             aria-current={page === currentPage ? 'page' : undefined}
             className={cn(
                "inline-flex items-center border-t-2 px-4 pt-4 text-sm font-medium",
                page === currentPage
                  ? "border-indigo-500 text-indigo-600 dark:border-indigo-400 dark:text-indigo-400"
                  : "border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-300 dark:hover:border-gray-600"
             )}
          >
            {page}
          </button>
        ))}
      </div>
      <div className="-mt-px flex w-0 flex-1 justify-end">
        <button
          onClick={() => onPageChange?.(Math.min(totalPages, currentPage + 1))}
          disabled={currentPage === totalPages}
          className="inline-flex items-center border-t-2 border-transparent pl-1 pt-4 text-sm font-medium text-gray-500 hover:border-gray-300 hover:text-gray-700 disabled:opacity-50 disabled:hover:border-transparent disabled:hover:text-gray-500 dark:text-gray-400 dark:hover:text-gray-300"
        >
          Next
          <ChevronRight className="ml-3 h-5 w-5 text-gray-400" aria-hidden="true" />
        </button>
      </div>
    </nav>
  )
}

import { cn } from "@/lib/utils";
import Link from "next/link";
import { Check } from "lucide-react";

export interface Step {
  id: string | number;
  name: string;
  href?: string;
  status: 'complete' | 'current' | 'upcoming';
}

interface StepsProps {
  steps: Step[];
  className?: string;
  onStepClick?: (step: Step) => void;
}

export default function Steps({ steps, className, onStepClick }: StepsProps) {
  return (
    <nav aria-label="Progress" className={cn(className)}>
      <ol role="list" className="overflow-hidden rounded-md bg-white border border-gray-200 dark:bg-gray-800 dark:border-gray-700 lg:flex lg:rounded-none lg:border-0 lg:border-r lg:border-l lg:border-gray-200 lg:dark:border-gray-700">
        {steps.map((step, stepIdx) => (
          <li key={step.id} className="relative overflow-hidden lg:flex-1">
            <div className={cn(
                "border border-gray-200 overflow-hidden lg:border-0",
                stepIdx === 0 ? "rounded-t-md border-b-0 lg:rounded-none" : "",
                stepIdx === steps.length - 1 ? "rounded-b-md border-t-0 lg:rounded-none" : ""
            )}>
              {/* Button or Link wrapper */}
              <button
                onClick={(e) => {
                    if (onStepClick) {
                        e.preventDefault();
                        onStepClick(step);
                    }
                    if (!step.href) e.preventDefault();
                }}
                className={cn(
                    "group flex w-full items-center",
                    step.href || onStepClick ? "cursor-pointer" : "cursor-default"
                )}
              >
                  <span
                    className={cn(
                      "absolute top-0 left-0 h-full w-1 bg-transparent group-hover:bg-gray-200 lg:bottom-0 lg:top-auto lg:h-1 lg:w-full",
                      step.status === 'current' ? "bg-indigo-600 dark:bg-indigo-500" : "",
                      step.status === 'complete' ? "bg-indigo-600 dark:bg-indigo-500" : "bg-transparent"
                    )}
                    aria-hidden="true"
                  />
                  <span className={cn(
                      "flex items-start px-6 py-5 text-sm font-medium lg:pl-9",
                      stepIdx !== 0 ? "lg:pl-9" : ""
                  )}>
                    <span className="flex-shrink-0">
                      {step.status === 'complete' ? (
                        <span className="flex h-10 w-10 items-center justify-center rounded-full bg-indigo-600">
                          <Check className="h-6 w-6 text-white" aria-hidden="true" />
                        </span>
                      ) : step.status === 'current' ? (
                        <span className="flex h-10 w-10 items-center justify-center rounded-full border-2 border-indigo-600">
                          <span className="text-indigo-600 dark:text-indigo-400">{String(step.id).padStart(2, '0')}</span>
                        </span>
                      ) : (
                        <span className="flex h-10 w-10 items-center justify-center rounded-full border-2 border-gray-300 dark:border-gray-600">
                          <span className="text-gray-500 dark:text-gray-400">{String(step.id).padStart(2, '0')}</span>
                        </span>
                      )}
                    </span>
                    <span className="ml-4 mt-0.5 flex min-w-0 flex-col">
                      <span className={cn("text-xs font-semibold uppercase tracking-wide", step.status === 'current' ? "text-indigo-600 dark:text-indigo-400" : "text-gray-500 dark:text-gray-400")}>
                         {step.status === 'complete' ? 'Completed' : step.status === 'current' ? 'Current' : 'Upcoming'}
                       </span>
                      <span className="text-sm font-medium text-gray-900 dark:text-white">{step.name}</span>
                    </span>
                  </span>
                </button>
              
                {stepIdx !== 0 ? (
                  <>
                    {/* Separator for LG screens */}
                    <div className="hidden lg:block absolute inset-0 left-0 top-0 hidden w-3 lg:block" aria-hidden="true">
                      <svg
                        className="h-full w-full text-gray-300 dark:text-gray-600"
                        viewBox="0 0 12 82"
                        preserveAspectRatio="none"
                        fill="none"
                      >
                        <path d="M0.5 0V31L10.5 41L0.5 51V82" vectorEffect="non-scaling-stroke" stroke="currentcolor" />
                      </svg>
                    </div>
                  </>
                ) : null}
            </div>
          </li>
        ))}
      </ol>
    </nav>
  )
}

