                    </Tooltip>
                }
                value={message}
                variant="bordered"
                onValueChange={setMessage}
                {...props}
            />
        );
    },
);

MessagingChatInput.displayName = "MessagingChatInput";

export default MessagingChatInput;

"use client";

import type { ChipProps, Selection } from "@heroui/react";

import * as React from "react";
import {
    Button,
    Input,
    Chip,
    User,
    Dropdown,
    DropdownTrigger,
    DropdownMenu,
    DropdownItem,
    Table,
    TableHeader,
    TableColumn,
    TableBody,
    TableRow,
    TableCell,
    Tabs,
    Tab,
    Card,
    CardBody,
} from "@heroui/react";
import { ChevronDownIcon, SearchIcon } from "@heroui/shared-icons";
import { capitalize } from "@heroui/shared-utils";
import { MoreVertical } from "lucide-react";

import { columns, rolesOptions, statusOptions, users } from "./team-data";

interface TeamManageTableProps {
    className?: string;
}
const statusColorMap: Record<string, ChipProps["color"]> = {
    active: "success",
    pending: "danger",
    vacation: "warning",
};

type UserType = (typeof users)[0];
const TeamManageTable = React.forwardRef<HTMLDivElement, TeamManageTableProps>(() => {
    const [filterValue, setFilterValue] = React.useState("");
    const [selectedKeys, setSelectedKeys] = React.useState<Selection>(new Set([]));
    const [rolesFilter, setRolesFilter] = React.useState<Selection>("all");
    const [statusFilter, setStatusFilter] = React.useState<Selection>("all");

    const hasSearchFilter = Boolean(filterValue);

    const filteredItems = React.useMemo(() => {
        let filteredUsers = [...users];

        if (hasSearchFilter) {
            filteredUsers = filteredUsers.filter((user) =>
                user.name.toLowerCase().includes(filterValue.toLowerCase()),
            );
        }
        if (rolesFilter !== "all" && Array.from(rolesFilter).length !== rolesOptions.length) {
            filteredUsers = filteredUsers.filter((user) =>
                Array.from(rolesFilter).includes(user.role.toLowerCase()),
            );
        }
        if (statusFilter !== "all" && Array.from(statusFilter).length !== statusOptions.length) {
            filteredUsers = filteredUsers.filter((user) =>
                Array.from(statusFilter).includes(user.status.toLowerCase()),
            );
        }

        return filteredUsers;
    }, [filterValue, rolesFilter, statusFilter, hasSearchFilter]);

    const renderCell = React.useCallback((user: UserType, columnKey: React.Key) => {
        const cellValue = user[columnKey as keyof UserType];

        switch (columnKey) {
            case "name":
                return (
                    <User
                        avatarProps={{ radius: "lg", src: user.avatar }}
                        description={user.email}
                        name={cellValue}
                    >
                        {user.email}
                    </User>
                );
            case "role":
                return (
                    <div className="flex flex-col">
                        <p className="text-bold  text-small capitalize text-default-500">{cellValue}</p>
                    </div>
                );
            case "status":
                return (
                    <Chip className="capitalize" color={statusColorMap[user.status]} size="sm" variant="flat">
                        {cellValue}
                    </Chip>
                );
            case "actions":
                return (
                    <div className="relative flex items-center justify-end gap-2">
                        <Dropdown>
                            <DropdownTrigger>
                                <Button isIconOnly size="sm" variant="light">
                                    <MoreVertical className="h-6 w-6 text-default-500" />
                                </Button>
                            </DropdownTrigger>
                            <DropdownMenu>
                                <DropdownItem key="view">View</DropdownItem>
                                <DropdownItem key="edit">Edit</DropdownItem>
                                <DropdownItem key="delete">Delete</DropdownItem>
                            </DropdownMenu>
                        </Dropdown>
                    </div>
                );
            default:
                return cellValue;
        }
    }, []);

    const onSearchChange = React.useCallback((value?: string) => {
        if (value) {
            setFilterValue(value);
        } else {
            setFilterValue("");
        }
    }, []);

    const onClear = React.useCallback(() => {
        setFilterValue("");
    }, []);

    const topContent = React.useMemo(() => {
        return (
            <div>
                <div className="flex items-center justify-between gap-3">
                    <Input
                        isClearable
                        className="w-full"
                        placeholder="Search by name..."
                        startContent={<SearchIcon />}
                        value={filterValue}
                        onClear={() => onClear()}
                        onValueChange={onSearchChange}
                    />
                    <div className="flex gap-3">
                        <Dropdown
                            classNames={{
                                trigger: "bg-default-100",
                            }}
                        >
                            <DropdownTrigger className="hidden sm:flex">
                                <Button endContent={<ChevronDownIcon className="text-small" />} variant="flat">
                                    All Team Roles
                                </Button>
                            </DropdownTrigger>
                            <DropdownMenu
                                disallowEmptySelection
                                aria-label="Table Columns"
                                closeOnSelect={false}
                                selectedKeys={rolesFilter}
                                selectionMode="multiple"
                                onSelectionChange={setRolesFilter}
                            >
                                {rolesOptions.map((status) => (
                                    <DropdownItem key={status.uid} className="capitalize">
                                        {capitalize(status.name)}
                                    </DropdownItem>
                                ))}
                            </DropdownMenu>
                        </Dropdown>
                    </div>
                </div>
                <Tabs
                    aria-label="roles"
                    className={"mt-3"}
                    variant={"underlined"}
                    onSelectionChange={(key) => {
                        if (key === "pending-invitations") {
                            setStatusFilter(new Set(["pending"]));
                        } else {
                            setStatusFilter("all");
                        }
                    }}
                >
                    <Tab key="members" title="Members" />
                    <Tab key="pending-invitations" title="Pending Invitations" />
                </Tabs>
            </div>
        );
    }, [filterValue, rolesFilter, onSearchChange, onClear, rolesFilter]);

    return (
        <Card className={"border border-default-200 bg-transparent"} shadow="none">
            <CardBody>
                <Table
                    hideHeader
                    isHeaderSticky
                    aria-label="Team Manage Table"
                    checkboxesProps={{
                        classNames: {
                            wrapper: ["after:bg-foreground after:text-background text-background"],
                        },
                    }}
                    classNames={{
                        wrapper: "max-h-[382px] bg-transparent p-0 border-none shadow-none",
                    }}
                    selectedKeys={selectedKeys}
                    selectionMode="multiple"
                    topContent={topContent}
                    topContentPlacement="outside"
                    onSelectionChange={setSelectedKeys}
                >
                    <TableHeader columns={columns}>
                        {(column) => (
                            <TableColumn
                                key={column.uid}
                                align={column.uid === "actions" ? "center" : "start"}
                                allowsSorting={column.sortable}
                            >
                                {column.name}
                            </TableColumn>
                        )}
                    </TableHeader>
                    <TableBody emptyContent={"No users found"} items={filteredItems}>
                        {(item) => (
                            <TableRow key={item.id}>
                                {(columnKey) => <TableCell>{renderCell(item, columnKey)}</TableCell>}
                            </TableRow>
                        )}
                    </TableBody>
                </Table>
            </CardBody>
        </Card>
    );
});

TeamManageTable.displayName = "TeamManageTable";

export default TeamManageTable;

"use client";
import type { ModalProps } from "@heroui/react";

import React from "react";
import { Drawer, DrawerBody, DrawerContent } from "@heroui/react";
import { cn } from "@heroui/react";

// Fallback for TRANSITION_EASINGS if not available
const TRANSITION_EASINGS = {
    easeOut: [0.0, 0.0, 0.2, 1.0],
};

const SidebarDrawer = React.forwardRef<
    HTMLDivElement,
    ModalProps & {
        sidebarWidth?: number;
        sidebarPlacement?: "left" | "right";
    }
>(
    (
        {
            children,
            className,
            onOpenChange,
            isOpen,
            sidebarWidth = 288,
            classNames = {},
            sidebarPlacement = "left",
            motionProps: drawerMotionProps,
            ...props
        },
        ref,
    ) => {
        const motionProps = React.useMemo(() => {
            if (!!drawerMotionProps && typeof drawerMotionProps === "object") {
                return drawerMotionProps;
            }

            return {
                variants: {
                    enter: {
                        x: 0,
                        transition: {
                            x: {
                                duration: 0.3,
                                ease: TRANSITION_EASINGS.easeOut,
                            },
                        },
                    },
                    exit: {
                        x: sidebarPlacement == "left" ? -sidebarWidth : sidebarWidth,
                        transition: {
                            x: {
                                duration: 0.2,
                                ease: TRANSITION_EASINGS.easeOut,
                            },
                        },
                    },
                },
            };
        }, [sidebarWidth, sidebarPlacement, drawerMotionProps]);

        return (
            <>
                <Drawer
                    ref={ref}
                    {...props}
                    classNames={{
                        ...classNames,
                        wrapper: cn("!w-[var(--sidebar-width)]", classNames?.wrapper, {
                            "!items-start !justify-start ": sidebarPlacement === "left",
                            "!items-end !justify-end": sidebarPlacement === "right",
                        }),
                        base: cn(
                            "w-[var(--sidebar-width)] !m-0 p-0 h-full max-h-full",
                            classNames?.base,
                            className,
                            {
                                "inset-y-0 left-0 max-h-[none] rounded-l-none !justify-start":
                                    sidebarPlacement === "left",
                                "inset-y-0 right-0 max-h-[none] rounded-r-none !justify-end":
                                    sidebarPlacement === "right",
                            },
                        ),
                        body: cn("p-0", classNames?.body),
                        closeButton: cn("z-50", classNames?.closeButton),
                    }}
                    isOpen={isOpen}
                    motionProps={motionProps}
                    radius="none"
                    scrollBehavior="inside"
                    style={{
                        // @ts-ignore
                        "--sidebar-width": `${sidebarWidth}px`,
                    }}
                    onOpenChange={onOpenChange}
                >
                    <DrawerContent>
                        <DrawerBody>{children}</DrawerBody>
                    </DrawerContent>
                </Drawer>
                <div
                    className={cn(
                        "hidden h-full max-w-[var(--sidebar-width)] overflow-x-hidden overflow-y-scroll sm:flex",
                        className,
                    )}
                >
                    {children}
                </div>
            </>
        );
    },
);

SidebarDrawer.displayName = "SidebarDrawer";

export default SidebarDrawer;

/**
 * CartItem Component
 * 
 * Individual cart line item with quantity controls.
 * Used in CartDrawer and Cart page.
 * 
 * BLIND QUOTE MODE:
 * - NO prices displayed
 * - Shows product, dates (if set), quantity
 * - Pricing is provided by admin quote only
 * 
 * Design: Dark cinema theme, compact layout
 */
'use client'

import { memo, useCallback } from 'react'
import Image from 'next/image'
import Link from 'next/link'
import { Trash2, Calendar } from 'lucide-react'
import { CartItem as CartItemType, CartItemDates, useCartStore } from '@/stores'
import { calculateRentalDays } from '@/lib/domain/pricing'
import { cn } from '@/lib/utils'

interface CartItemProps {
  item: CartItemType
  lng: string
  compact?: boolean
  className?: string
}

export const CartItemComponent = memo(function CartItemComponent({
  item,
  lng,
  compact = false,
  className,
}: CartItemProps) {
  const updateQuantity = useCartStore((state) => state.updateQuantity)
  const updateItemDates = useCartStore((state) => state.updateItemDates)
  const removeItem = useCartStore((state) => state.removeItem)

  const { product, quantity, dates } = item
  
  // Only show rental days if dates are actually set (not placeholder)
  const hasDates = dates.start && dates.end && dates.start !== '' && dates.end !== ''
  const rentalDays = hasDates ? calculateRentalDays(dates.start, dates.end) : 0

  const handleQuantityChange = useCallback(
    (newQty: number) => {
      if (newQty <= 0) {
        removeItem(item.id)
      } else {
        updateQuantity(item.id, newQty)
      }
    },
    [item.id, updateQuantity, removeItem]
  )

  const handleDateChange = useCallback(
    (newDates: Partial<CartItemDates>) => {
      updateItemDates(item.id, { ...dates, ...newDates })
    },
    [item.id, dates, updateItemDates]
  )

  const handleRemove = useCallback(() => {
    removeItem(item.id)
  }, [item.id, removeItem])

  const productUrl = `/${lng}/equipment/${product.slug}`

  if (compact) {
    return (
      <div className={cn('flex gap-3 py-3', className)}>
        {/* Thumbnail */}
        <Link href={productUrl} className="relative w-16 h-16 rounded-lg overflow-hidden flex-shrink-0 bg-zinc-800">
          {product.imageUrl ? (
            <Image
              src={product.imageUrl}
              alt={product.name}
              fill
              sizes="64px"
              className="object-cover"
            />
          ) : (
            <div className="w-full h-full flex items-center justify-center">
              <svg className="w-6 h-6 text-zinc-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
            </div>
          )}
        </Link>

        {/* Info */}
        <div className="flex-1 min-w-0">
          <Link href={productUrl} className="text-sm font-medium text-zinc-100 hover:text-white line-clamp-1">
            {product.name}
          </Link>
          <p className="text-xs text-zinc-500 mt-0.5">
            {hasDates ? (
              <>{rentalDays} day{rentalDays !== 1 ? 's' : ''} ├ù {quantity}</>
            ) : (
              <>Qty: {quantity}</>
            )}
          </p>
          {/* BLIND QUOTE: No price shown */}
          <p className="text-xs text-zinc-400 mt-1 italic">
            Price on quote
          </p>
        </div>

        {/* Remove */}
        <button
          onClick={handleRemove}
          className="p-1.5 text-zinc-500 hover:text-red-400 transition-colors"
          aria-label="Remove item"
        >
          <Trash2 className="w-4 h-4" />
        </button>
      </div>
    )
  }

  return (
    <div className={cn('flex gap-4 py-4 border-b border-zinc-800', className)}>
      {/* Image */}
      <Link href={productUrl} className="relative w-24 h-24 rounded-lg overflow-hidden flex-shrink-0 bg-zinc-800">
        {product.imageUrl ? (
          <Image
            src={product.imageUrl}
            alt={product.name}
            fill
            sizes="96px"
            className="object-cover"
          />
        ) : (
          <div className="w-full h-full flex items-center justify-center">
            <svg className="w-8 h-8 text-zinc-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
          </div>
        )}
      </Link>

      {/* Content */}
      <div className="flex-1 min-w-0">
        <div className="flex items-start justify-between gap-2">
          <Link href={productUrl} className="text-base font-medium text-zinc-100 hover:text-white line-clamp-2">
            {product.name}
          </Link>
          <button
            onClick={handleRemove}
            className="p-1.5 text-zinc-500 hover:text-red-400 transition-colors flex-shrink-0"
            aria-label="Remove item"
          >
            <Trash2 className="w-4 h-4" />
          </button>
        </div>

        {/* Date Display - only show if dates are set */}
        {hasDates ? (
          <div className="flex items-center gap-1.5 mt-2 text-xs text-zinc-400">
            <Calendar className="w-3.5 h-3.5" />
            <span>{dates.start} ΓåÆ {dates.end}</span>
            <span className="text-zinc-600">({rentalDays} day{rentalDays !== 1 ? 's' : ''})</span>
          </div>
        ) : (
          <p className="mt-2 text-xs text-zinc-500 italic">
            Dates to be selected at checkout
          </p>
        )}

        {/* Quantity Controls - No price shown (BLIND QUOTE) */}
        <div className="flex items-center justify-between mt-3">
          {/* Quantity Controls */}
          <div className="flex items-center gap-2">
            <button
              onClick={() => handleQuantityChange(quantity - 1)}
              className="w-7 h-7 flex items-center justify-center bg-zinc-800 border border-zinc-700 rounded text-zinc-300 hover:bg-zinc-700 text-sm"
            >
              ΓêÆ
            </button>
            <span className="w-8 text-center text-sm text-zinc-100">{quantity}</span>
            <button
              onClick={() => handleQuantityChange(quantity + 1)}
              className="w-7 h-7 flex items-center justify-center bg-zinc-800 border border-zinc-700 rounded text-zinc-300 hover:bg-zinc-700 text-sm"
            >
              +
            </button>
          </div>

          {/* BLIND QUOTE: No price, just a note */}
          <div className="text-right">
            <p className="text-xs text-zinc-500 italic">Price on quote</p>
          </div>
        </div>
      </div>
    </div>
  )
})

/**
 * Chat Widget
 * 
 * Floating AI chatbot interface for equipment recommendations.
 * Uses Vercel AI SDK v4 useChat hook for streaming.
 * Styled with HeroUI components for consistency.
 */

'use client'

import { useState, useRef, useEffect } from 'react'
import { useChat } from 'ai/react'
import { useParams } from 'next/navigation'
import { motion, AnimatePresence } from 'framer-motion'
import { Button, Card, CardBody, CardHeader, CardFooter, Chip } from '@heroui/react'
import { cn } from '@heroui/react'
import {
    MessageCircle,
    X,
    Sparkles,
    Camera,
    Lightbulb,
    Search,
    Bot,
} from 'lucide-react'
import { ChatProductCarousel } from './chat-product-card'
import MessageCard from './message-card'
import PromptInput from './prompt-input'
import { StoreInfoCard, StoreInfoData } from './store-info-card'
import { NavigationCard, NavigationCardData } from './navigation-card'

// Starter suggestions
const STARTER_CHIPS = [
    { icon: Camera, text: '≡ƒÄÑ Recommend a Camera Kit', query: 'I need a camera kit for a commercial shoot' },
    { icon: Lightbulb, text: '≡ƒÆí Lighting Package', query: 'What lighting do you recommend for an interview setup?' },
    { icon: Search, text: '≡ƒôì Location & Hours', query: 'Where are you located and what are your opening hours?' },
]

export function ChatWidget() {
    const [isOpen, setIsOpen] = useState(false)
    const [hasInteracted, setHasInteracted] = useState(false)
    const messagesEndRef = useRef<HTMLDivElement>(null)
    const inputRef = useRef<HTMLTextAreaElement>(null)
    const params = useParams()
    const lng = (params?.lng as string) || 'en'

    // AI SDK v4 useChat hook
    const {
        messages,
        input,
        setInput,
        handleSubmit,
        isLoading,
        append,
        error,
    } = useChat({
        api: '/api/chat',
        id: 'rental-concierge',
    })

    // Auto-scroll to bottom on new messages
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' })
    }, [messages])

    // Focus input when opened
    useEffect(() => {
        if (isOpen && inputRef.current) {
            inputRef.current.focus()
        }
    }, [isOpen])

    const handleStarterClick = (query: string) => {
        setHasInteracted(true)
        append({ role: 'user', content: query })
    }

    const onSubmit = () => {
        if (!input.trim()) return
        setHasInteracted(true)
        handleSubmit(new Event('submit') as unknown as React.FormEvent<HTMLFormElement>)
    }

    return (
        <>
            {/* Floating Action Button */}
            <AnimatePresence>
                {!isOpen && (
                    <motion.div
                        initial={{ scale: 0, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        exit={{ scale: 0, opacity: 0 }}
                        className="fixed bottom-6 right-6 z-40"
                    >
                        <Button
                            isIconOnly
                            className="w-14 h-14 bg-primary shadow-lg shadow-primary/30 hover:shadow-primary/50"
                            radius="full"
                            size="lg"
                            onPress={() => setIsOpen(true)}
                            aria-label="Open chat"
                        >
                            <MessageCircle className="w-6 h-6" />
                        </Button>
                        {/* Online indicator */}
                        <span className="absolute -top-1 -right-1 w-4 h-4 bg-success rounded-full border-2 border-background animate-pulse" />
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Chat Window */}
            <AnimatePresence>
                {isOpen && (
                    <motion.div
                        initial={{ opacity: 0, y: 20, scale: 0.95 }}
                        animate={{ opacity: 1, y: 0, scale: 1 }}
                        exit={{ opacity: 0, y: 20, scale: 0.95 }}
                        transition={{ type: 'spring', damping: 25, stiffness: 300 }}
                        className="fixed bottom-6 right-6 z-40 w-[400px] max-w-[calc(100vw-48px)] h-[600px] max-h-[calc(100vh-100px)]"
                    >
                        <Card className="h-full bg-content1/95 backdrop-blur-xl border border-default-200 shadow-2xl">
                            {/* Header */}
                            <CardHeader className="flex items-center justify-between px-4 py-3 border-b border-default-200">
                                <div className="flex items-center gap-3">
                                    <div className="w-9 h-9 rounded-full bg-gradient-to-br from-primary to-secondary flex items-center justify-center">
                                        <Sparkles className="w-5 h-5 text-white" />
                                    </div>
                                    <div>
                                        <h3 className="text-small font-semibold text-foreground">Rental Concierge</h3>
                                        <p className="text-tiny text-default-500">Powered by AI</p>
                                    </div>
                                </div>
                                <Button
                                    isIconOnly
                                    radius="lg"
                                    size="sm"
                                    variant="light"
                                    onPress={() => setIsOpen(false)}
                                >
                                    <X className="w-5 h-5 text-default-500" />
                                </Button>
                            </CardHeader>

                            {/* Messages Area - overscroll-contain prevents page scroll when chat is scrolled */}
                            <CardBody
                                className="flex-1 overflow-y-auto p-4 space-y-4 overscroll-contain"
                                style={{ overscrollBehavior: 'contain' }}
                            >
                                {/* Empty State - Starter Chips */}
                                {messages.length === 0 && !hasInteracted && (
                                    <div className="h-full flex flex-col items-center justify-center text-center px-4">
                                        <div className="w-16 h-16 rounded-full bg-gradient-to-br from-primary/20 to-secondary/20 border border-primary/30 flex items-center justify-center mb-4">
                                            <Bot className="w-8 h-8 text-primary" />
                                        </div>
                                        <h4 className="text-large font-semibold text-foreground mb-2">
                                            Welcome to TFS Film Equipment
                                        </h4>
                                        <p className="text-small text-default-500 mb-6">
                                            I can help you find the perfect gear for your shoot.
                                        </p>
                                        <div className="space-y-2 w-full max-w-xs">
                                            {STARTER_CHIPS.map((chip, index) => (
                                                <motion.div
                                                    key={index}
                                                    initial={{ opacity: 0, y: 10 }}
                                                    animate={{ opacity: 1, y: 0 }}
                                                    transition={{ delay: index * 0.1 }}
                                                >
                                                    <Chip
                                                        as="button"
                                                        className="w-full h-auto py-3 px-4 cursor-pointer"
                                                        classNames={{
                                                            base: "bg-content2 hover:bg-content3 border border-default-200",
                                                            content: "text-small text-default-600 text-left",
                                                        }}
                                                        radius="lg"
                                                        variant="flat"
                                                        onClick={() => handleStarterClick(chip.query)}
                                                    >
                                                        {chip.text}
                                                    </Chip>
                                                </motion.div>
                                            ))}
                                        </div>
                                    </div>
                                )}

                                {/* Message Thread */}
                                {messages.map((message) => {
                                    // Skip rendering empty assistant messages (happens during tool calls)
                                    const hasContent: boolean = Boolean(message.content && message.content.trim().length > 0);
                                    const isAssistant = message.role === 'assistant';

                                    return (
                                        <div key={message.id} className="space-y-3">
                                            {/* Only render MessageCard if there's content, or it's a user message */}
                                            {(hasContent || !isAssistant) && (
                                                <MessageCard
                                                    role={message.role as 'user' | 'assistant'}
                                                    message={message.content}
                                                    showFeedback={isAssistant && Boolean(hasContent)}
                                                />
                                            )}

                                            {/* Tool invocations - Generative UI */}
                                            {message.role === 'assistant' && message.toolInvocations?.map((tool) => {
                                                if (tool.state === 'result') {
                                                    // Handle equipment lookup
                                                    if (tool.toolName === 'lookup_equipment') {
                                                        const result = tool.result as { equipment?: Array<{ id: string; name: string; slug: string; category?: string; description?: string; imageUrl?: string; isAvailable?: boolean }> }
                                                        if (result.equipment && result.equipment.length > 0) {
                                                            return (
                                                                <div key={tool.toolCallId} className="ml-11">
                                                                    <ChatProductCarousel
                                                                        products={result.equipment}
                                                                        lng={lng}
                                                                    />
                                                                </div>
                                                            )
                                                        }
                                                    }
                                                    // Handle store info
                                                    if (tool.toolName === 'get_store_info') {
                                                        const result = tool.result as StoreInfoData
                                                        return (
                                                            <div key={tool.toolCallId} className="ml-11">
                                                                <StoreInfoCard data={result} />
                                                            </div>
                                                        )
                                                    }
                                                    // Handle site navigation
                                                    if (tool.toolName === 'navigate_site') {
                                                        const result = tool.result as NavigationCardData
                                                        return (
                                                            <div key={tool.toolCallId} className="ml-11">
                                                                <NavigationCard data={result} />
                                                            </div>
                                                        )
                                                    }
                                                }
                                                return null
                                            })}
                                        </div>
                                    );
                                })}

                                {/* Loading indicator */}
                                {isLoading && (
                                    <MessageCard
                                        role="assistant"
                                        message=""
                                        isLoading={true}
                                    />
                                )}

                                {/* Error state */}
                                {error && (
                                    <div className="px-4 py-3 bg-danger/10 border border-danger/30 rounded-medium">
                                        <p className="text-small text-danger">
                                            Something went wrong. Please try again.
                                        </p>
                                    </div>
                                )}

                                <div ref={messagesEndRef} />
                            </CardBody>

                            {/* Input Area */}
                            <CardFooter className="flex-col gap-2 p-4 border-t border-default-200">
                                <PromptInput
                                    ref={inputRef}
                                    value={input}
                                    onChange={setInput}
                                    onSubmit={onSubmit}
                                    isLoading={isLoading}
                                    placeholder="Ask about equipment..."
                                />
                                <p className="text-tiny text-default-400 text-center">
                                    AI can make mistakes. Verify important info.
                                </p>
                            </CardFooter>
                        </Card>
                    </motion.div>
                )}
            </AnimatePresence>
        </>
    )
}

"use client";

import { useState } from 'react';
import { Dialog, DialogBackdrop, DialogPanel, TransitionChild } from '@headlessui/react';
import {
    X,
    Home,
    FileText,
    ClipboardList,
    User,
    Settings,
    LogOut,
    Menu,
    LucideIcon
} from 'lucide-react';
import { cn } from '@/lib/utils';
import Link from 'next/link';
import { usePathname } from 'next/navigation';
import { useAuthStore } from '@/stores/auth-store';
import { ROLES } from '@/types/auth';

interface NavigationItem {
    name: string;
    href: string;
    icon: LucideIcon;
}

interface DashboardSidebarProps {
    children: React.ReactNode;
    lng: string;
}

export default function DashboardSidebar({
    children,
    lng
}: DashboardSidebarProps) {
    const [sidebarOpen, setSidebarOpen] = useState(false);
    const pathname = usePathname();
    const user = useAuthStore((state) => state.user);

    const logout = () => {
        // Create a form to submit the logout action
        const form = document.createElement('form');
        form.method = 'POST';
        form.action = '/logout'; // We'll need a route handler for this
        document.body.appendChild(form);
        form.submit();
    };

    const navigation: NavigationItem[] = [
        { name: 'Dashboard', href: `/${lng}/dashboard`, icon: Home },
        // Placeholder links until User Views are built
        // { name: 'Quotes', href: `/${lng}/dashboard/quotes`, icon: FileText },
        // { name: 'Requests', href: `/${lng}/dashboard/requests`, icon: ClipboardList },
        { name: 'Profile', href: `/${lng}/dashboard/profile`, icon: User },
        { name: 'Settings', href: `/${lng}/dashboard/settings`, icon: Settings },
    ];

    // Conditionally add Admin Link
    // We check if the custom 'role' field is set to 'admin'
    // This assumes the user record has this field populated correctly.
    if (user && (user as any).role === ROLES.ADMIN) {
        navigation.push({ name: 'Admin Operations', href: `/${lng}/admin/requests`, icon: ClipboardList });
    }

    const isCurrent = (href: string) => {
        if (href === `/${lng}/dashboard` && pathname === href) return true;
        if (href !== `/${lng}/dashboard` && pathname?.startsWith(href)) return true;
        return false;
    };

    return (
        <div>
            {/* Mobile Sidebar Dialog */}
            <Dialog open={sidebarOpen} onClose={setSidebarOpen} className="relative z-50 lg:hidden">
                <DialogBackdrop
                    transition
                    className="fixed inset-0 bg-gray-900/80 transition-opacity duration-300 ease-linear data-closed:opacity-0"
                />

                <div className="fixed inset-0 flex">
                    <DialogPanel
                        transition
                        className="relative mr-16 flex w-full max-w-xs flex-1 transform transition duration-300 ease-in-out data-closed:-translate-x-full"
                    >
                        <TransitionChild>
                            <div className="absolute top-0 left-full flex w-16 justify-center pt-5 duration-300 ease-in-out data-closed:opacity-0">
                                <button type="button" onClick={() => setSidebarOpen(false)} className="-m-2.5 p-2.5">
                                    <span className="sr-only">Close sidebar</span>
                                    <X aria-hidden="true" className="size-6 text-white" />
                                </button>
                            </div>
                        </TransitionChild>

                        {/* Mobile Sidebar Content */}
                        <div className="flex grow flex-col gap-y-5 overflow-y-auto bg-gray-900 px-6 pb-2 ring-1 ring-white/10">
                            <div className="flex h-16 shrink-0 items-center">
                                <span className="text-xl font-bold text-white tracking-widest">ZOUSKYM</span>
                            </div>
                            <nav className="flex flex-1 flex-col">
                                <ul role="list" className="flex flex-1 flex-col gap-y-7">
                                    <li>
                                        <ul role="list" className="-mx-2 space-y-1">
                                            {navigation.map((item) => (
                                                <li key={item.name}>
                                                    <Link
                                                        href={item.href}
                                                        className={cn(
                                                            isCurrent(item.href)
                                                                ? 'bg-gray-800 text-white'
                                                                : 'text-gray-400 hover:bg-gray-800 hover:text-white',
                                                            'group flex gap-x-3 rounded-md p-2 text-sm/6 font-semibold',
                                                        )}
                                                    >
                                                        <item.icon aria-hidden="true" className="size-6 shrink-0" />
                                                        {item.name}
                                                    </Link>
                                                </li>
                                            ))}
                                        </ul>
                                    </li>

                                    <li className="-mx-6 mt-auto">
                                        <div className="flex items-center gap-x-4 px-6 py-3 text-sm/6 font-semibold text-white hover:bg-gray-800">
                                            {/* Avatar Placeholder */}
                                            <div className="size-8 rounded-full bg-gray-700 flex items-center justify-center text-xs">
                                                {user?.email?.charAt(0).toUpperCase() || 'U'}
                                            </div>
                                            <span className="sr-only">Your profile</span>
                                            <div className="flex flex-col">
                                                <span aria-hidden="true">{user?.email || 'User'}</span>
                                                <button onClick={logout} className="text-xs text-gray-400 text-left hover:text-white">Sign out</button>
                                            </div>
                                        </div>
                                    </li>
                                </ul>
                            </nav>
                        </div>
                    </DialogPanel>
                </div>
            </Dialog>

            {/* Static sidebar for desktop */}
            <div className="hidden lg:fixed lg:inset-y-0 lg:z-50 lg:flex lg:w-72 lg:flex-col">
                <div className="flex grow flex-col gap-y-5 overflow-y-auto bg-gray-900 px-6">
                    <div className="flex h-16 shrink-0 items-center">
                        <span className="text-xl font-bold text-white tracking-widest">ZOUSKYM</span>
                    </div>
                    <nav className="flex flex-1 flex-col">
                        <ul role="list" className="flex flex-1 flex-col gap-y-7">
                            <li>
                                <ul role="list" className="-mx-2 space-y-1">
                                    {navigation.map((item) => (
                                        <li key={item.name}>
                                            <Link
                                                href={item.href}
                                                className={cn(
                                                    isCurrent(item.href)
                                                        ? 'bg-gray-800 text-white'
                                                        : 'text-gray-400 hover:bg-gray-800 hover:text-white',
                                                    'group flex gap-x-3 rounded-md p-2 text-sm/6 font-semibold',
                                                )}
                                            >
                                                <item.icon aria-hidden="true" className="size-6 shrink-0" />
                                                {item.name}
                                            </Link>
                                        </li>
                                    ))}
                                </ul>
                            </li>
                            <li className="-mx-6 mt-auto">
                                <div className="flex items-center gap-x-4 px-6 py-3 text-sm/6 font-semibold text-white hover:bg-gray-800">
                                    <div className="size-8 rounded-full bg-gray-700 flex items-center justify-center text-xs">
                                        {user?.email?.charAt(0).toUpperCase() || 'U'}
                                    </div>
                                    <span className="sr-only">Your profile</span>
                                    <div className="flex flex-col overflow-hidden">
                                        <span aria-hidden="true" className="truncate w-full">{user?.email || 'User'}</span>
                                        <button onClick={logout} className="text-xs text-gray-400 text-left hover:text-white flex items-center gap-1">
                                            <LogOut className="size-3" /> Sign out
                                        </button>
                                    </div>
                                </div>
                            </li>
                        </ul>
                    </nav>
                </div>
            </div>

            {/* Top bar mobile */}
            <div className="sticky top-0 z-40 flex items-center gap-x-6 bg-gray-900 px-4 py-4 shadow-xs sm:px-6 lg:hidden">
                <button type="button" onClick={() => setSidebarOpen(true)} className="-m-2.5 p-2.5 text-gray-400 lg:hidden">
                    <span className="sr-only">Open sidebar</span>
                    <Menu aria-hidden="true" className="size-6" />
                </button>
                <div className="flex-1 text-sm/6 font-semibold text-white">Dashboard</div>
                <Link href={`/${lng}/dashboard/profile`}>
                    <span className="sr-only">Your profile</span>
                    <div className="size-8 rounded-full bg-gray-700 flex items-center justify-center text-xs text-white">
                        {user?.email?.charAt(0).toUpperCase() || 'U'}
                    </div>
                </Link>
            </div>

            {/* Main Content Area */}
            <main className="py-10 lg:pl-72">
                <div className="px-4 sm:px-6 lg:px-8">
                    {children}
                </div>
            </main>
        </div>
    )
}

'use client'

/**
 * User Quotes List Component
 * 
 * Displays user's quote requests with status badges, rental dates,
 * and equipment preview. Cinema-grade dark theme styling.
 */

import { motion } from 'framer-motion'
import {
  Package,
  Calendar,
  Clock,
  CheckCircle2,
  AlertCircle,
  Loader2,
  FileText,
  ChevronRight,
  Camera,
  Eye,
  Download,
  FileCheck
} from 'lucide-react'
import type { DashboardQuote } from '@/lib/actions/dashboard'
import { format, parseISO, differenceInDays } from 'date-fns'

// ============================================================================
// Status Badge Component
// ============================================================================

type QuoteStatus = 'pending' | 'reviewing' | 'quoted' | 'confirmed' | 'rejected'

const statusConfig: Record<QuoteStatus, {
  label: string
  icon: React.ReactNode
  bgClass: string
  textClass: string
  dotClass: string
}> = {
  pending: {
    label: 'Pending',
    icon: <Clock className="w-3.5 h-3.5" />,
    bgClass: 'bg-amber-500/10 border-amber-500/20',
    textClass: 'text-amber-400',
    dotClass: 'bg-amber-500'
  },
  reviewing: {
    label: 'Under Review',
    icon: <Eye className="w-3.5 h-3.5" />,
    bgClass: 'bg-blue-500/10 border-blue-500/20',
    textClass: 'text-blue-400',
    dotClass: 'bg-blue-500'
  },
  quoted: {
    label: 'Quote Ready',
    icon: <FileCheck className="w-3.5 h-3.5" />,
    bgClass: 'bg-purple-500/10 border-purple-500/20',
    textClass: 'text-purple-400',
    dotClass: 'bg-purple-500'
  },
  confirmed: {
    label: 'Confirmed',
    icon: <CheckCircle2 className="w-3.5 h-3.5" />,
    bgClass: 'bg-emerald-500/10 border-emerald-500/20',
    textClass: 'text-emerald-400',
    dotClass: 'bg-emerald-500'
  },
  rejected: {
    label: 'Declined',
    icon: <AlertCircle className="w-3.5 h-3.5" />,
    bgClass: 'bg-red-500/10 border-red-500/20',
    textClass: 'text-red-400',
    dotClass: 'bg-red-500'
  }
}

function StatusBadge({ status }: { status: QuoteStatus }) {
  const config = statusConfig[status]

  return (
    <div className={`inline-flex items-center gap-1.5 px-2.5 py-1 rounded-full border ${config.bgClass}`}>
      <span className={`w-1.5 h-1.5 rounded-full ${config.dotClass} animate-pulse`} />
      <span className={config.textClass}>{config.icon}</span>
      <span className={`text-xs font-medium ${config.textClass}`}>{config.label}</span>
    </div>
  )
}

// ============================================================================
// Quote Card Component
// ============================================================================

interface QuoteCardProps {
  quote: DashboardQuote
  index: number
}

function QuoteCard({ quote, index }: QuoteCardProps) {
  const startDate = parseISO(quote.rentalStartDate)
  const endDate = parseISO(quote.rentalEndDate)
  const rentalDays = differenceInDays(endDate, startDate) + 1
  const createdDate = parseISO(quote.created)

  // Get first 3 items for preview
  const previewItems = quote.items.slice(0, 3)
  const remainingItems = quote.items.length - 3

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: index * 0.1, duration: 0.4 }}
      className="group relative bg-zinc-900/50 rounded-xl border border-white/5 overflow-hidden hover:border-white/10 transition-colors"
    >
      {/* Top accent line based on status */}
      <div
        className={`absolute top-0 left-0 right-0 h-0.5 ${quote.status === 'confirmed' ? 'bg-emerald-500' :
          quote.status === 'pending' ? 'bg-amber-500' :
            quote.status === 'reviewing' ? 'bg-blue-500' :
              'bg-red-500'
          }`}
      />

      <div className="p-6">
        {/* Header Row */}
        <div className="flex items-start justify-between mb-4">
          <div>
            <div className="flex items-center gap-2 mb-1">
              <FileText className="w-4 h-4 text-zinc-500" />
              <span className="text-xs text-zinc-500 font-mono">
                {quote.confirmationNumber || `#${quote.id.slice(0, 8).toUpperCase()}`}
              </span>
            </div>
            <p className="text-xs text-zinc-600">
              Submitted {format(createdDate, 'MMM d, yyyy')}
            </p>
          </div>
          <StatusBadge status={quote.status} />
        </div>

        {/* Rental Period */}
        <div className="flex items-center gap-3 mb-4 p-3 bg-zinc-800/50 rounded-lg">
          <div className="p-2 bg-white/5 rounded-lg">
            <Calendar className="w-5 h-5 text-white" />
          </div>
          <div className="flex-1">
            <p className="text-sm text-zinc-400">Rental Period</p>
            <p className="text-white font-medium">
              {format(startDate, 'MMM d')} ΓÇö {format(endDate, 'MMM d, yyyy')}
            </p>
          </div>
          <div className="text-right">
            <p className="text-2xl font-bold text-white">{rentalDays}</p>
            <p className="text-xs text-zinc-500">{rentalDays === 1 ? 'day' : 'days'}</p>
          </div>
        </div>

        {/* Equipment Preview */}
        <div className="mb-4">
          <div className="flex items-center gap-2 mb-2">
            <Package className="w-4 h-4 text-zinc-500" />
            <span className="text-sm text-zinc-400">
              {quote.itemCount} {quote.itemCount === 1 ? 'item' : 'items'}
            </span>
          </div>
          <div className="flex flex-wrap gap-2">
            {previewItems.map((item, i) => (
              <div
                key={i}
                className="flex items-center gap-2 px-2 py-1 bg-zinc-800/50 rounded-md"
              >
                {item.imageUrl ? (
                  <img
                    src={item.imageUrl}
                    alt={item.name}
                    className="w-6 h-6 rounded object-cover"
                  />
                ) : (
                  <Camera className="w-4 h-4 text-zinc-600" />
                )}
                <span className="text-xs text-zinc-300 truncate max-w-[120px]">
                  {item.name}
                </span>
                {item.quantity > 1 && (
                  <span className="text-xs text-zinc-500">├ù{item.quantity}</span>
                )}
              </div>
            ))}
            {remainingItems > 0 && (
              <div className="flex items-center px-2 py-1 bg-zinc-800/50 rounded-md">
                <span className="text-xs text-zinc-500">+{remainingItems} more</span>
              </div>
            )}
          </div>
        </div>

        {/* Estimated Price (if available) */}
        {quote.estimatedPrice && (quote.status === 'confirmed' || quote.status === 'quoted') && (
          <div className="flex items-center justify-between p-3 bg-emerald-500/5 border border-emerald-500/10 rounded-lg">
            <span className="text-sm text-emerald-400">Estimated Total</span>
            <span className="text-lg font-bold text-emerald-400">
              {quote.estimatedPrice.toLocaleString()} MAD
            </span>
          </div>
        )}

        {/* Download Quote PDF Button */}
        {quote.quotePdfUrl && (
          <a
            href={quote.quotePdfUrl}
            target="_blank"
            rel="noopener noreferrer"
            className="mt-2 w-full flex items-center justify-center gap-2 py-2.5 px-4 text-sm font-medium text-purple-400 bg-purple-500/10 border border-purple-500/20 rounded-lg hover:bg-purple-500/20 transition-colors"
          >
            <Download className="w-4 h-4" />
            Download Official Quote
          </a>
        )}

        {/* View Details Link */}
        <button
          className="mt-4 w-full flex items-center justify-center gap-2 py-2 text-sm text-zinc-400 hover:text-white transition-colors group/btn"
        >
          View Details
          <ChevronRight className="w-4 h-4 group-hover/btn:translate-x-1 transition-transform" />
        </button>
      </div>
    </motion.div>
  )
}

// ============================================================================
// Empty State Component
// ============================================================================

function EmptyState() {
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="flex flex-col items-center justify-center py-16 px-8 bg-zinc-900/30 rounded-xl border border-dashed border-white/10"
    >
      <div className="p-4 bg-zinc-800/50 rounded-full mb-4">
        <Package className="w-8 h-8 text-zinc-500" />
      </div>
      <h3 className="text-lg font-medium text-white mb-2">No Quotes Yet</h3>
      <p className="text-sm text-zinc-500 text-center max-w-sm mb-6">
        You haven&apos;t submitted any rental requests yet. Browse our equipment and submit a quote to get started.
      </p>
      <a
        href="/en/equipment"
        className="inline-flex items-center gap-2 px-4 py-2 bg-white text-zinc-900 font-medium rounded-lg hover:bg-zinc-100 transition-colors"
      >
        <Camera className="w-4 h-4" />
        Browse Equipment
      </a>
    </motion.div>
  )
}

// ============================================================================
// Loading State Component
// ============================================================================

function LoadingState() {
  return (
    <div className="flex flex-col items-center justify-center py-16">
      <Loader2 className="w-8 h-8 text-zinc-500 animate-spin mb-4" />
      <p className="text-sm text-zinc-500">Loading your quotes...</p>
    </div>
  )
}

// ============================================================================
// Main Component
// ============================================================================

interface UserQuotesListProps {
  quotes: DashboardQuote[]
  isLoading?: boolean
}

export function UserQuotesList({ quotes, isLoading = false }: UserQuotesListProps) {
  if (isLoading) {
    return <LoadingState />
  }

  if (quotes.length === 0) {
    return <EmptyState />
  }

  return (
    <div className="space-y-4">
      {/* Section Header */}
      <div className="flex items-center justify-between">
        <h2 className="text-lg font-semibold text-white">Your Rental Requests</h2>
        <span className="text-sm text-zinc-500">{quotes.length} total</span>
      </div>

      {/* Quotes Grid */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {quotes.map((quote, index) => (
          <QuoteCard key={quote.id} quote={quote} index={index} />
        ))}
      </div>
    </div>
  )
}

"use client";

