export const SkeletonFour = () => {
    return (
        <div className="h-60 md:h-60 flex flex-col items-center relative bg-transparent mt-10">
            <Globe className="absolute -right-10 md:-right-10 -bottom-80 md:-bottom-72" />
        </div>
    );
};

export const Globe = ({ className }: { className?: string }) => {
    const canvasRef = useRef<HTMLCanvasElement>(null);

    useEffect(() => {
        let phi = 0;

        if (!canvasRef.current) return;

        const globe = createGlobe(canvasRef.current, {
            devicePixelRatio: 2,
            width: 600 * 2,
            height: 600 * 2,
            phi: 0,
            theta: 0,
            dark: 1,
            diffuse: 1.2,
            mapSamples: 16000,
            mapBrightness: 6,
            baseColor: [0.1, 0.1, 0.1],
            markerColor: [0.6, 0.6, 0.6], // Gray markers as requested
            glowColor: [0.2, 0.2, 0.2],
            markers: [
                { location: [31.7917, -7.0926], size: 0.15 }, // Morocco (Bigger to take attention)
                { location: [34.0522, -118.2437], size: 0.05 }, // LA
                { location: [51.5072, -0.1276], size: 0.05 }, // London
                { location: [35.6762, 139.6503], size: 0.05 }, // Tokyo
                { location: [-33.8688, 151.2093], size: 0.05 }, // Sydney
                { location: [25.2048, 55.2708], size: 0.05 }, // Dubai
            ],
            onRender: (state) => {
                state.phi = phi;
                phi += 0.01;
            },
        });

        return () => {
            globe.destroy();
        };
    }, []);

    return (
        <canvas
            ref={canvasRef}
            style={{ width: 600, height: 600, maxWidth: "100%", aspectRatio: 1 }}
            className={className}
        />
    );
};

"use client";

import type { ImageProps } from "next/image";
import Image from "next/image";
import { LazyMotion, domAnimation, m, useAnimation } from "framer-motion";
import { useEffect, useState } from "react";

const animationVariants = {
    hidden: { opacity: 0 },
    visible: { opacity: 1 },
};

export const FadeInImage = (props: ImageProps) => {
    const [isLoaded, setIsLoaded] = useState(false);
    const animationControls = useAnimation();

    useEffect(() => {
        if (isLoaded) {
            animationControls.start("visible");
        }
    }, [isLoaded, animationControls]);

    return (
        <LazyMotion features={domAnimation}>
            <m.div
                animate={animationControls}
                initial="hidden"
                transition={{ duration: 0.5, ease: "easeOut" }}
                variants={animationVariants}
                className={props.className}
            >
                <Image {...props} onLoad={() => setIsLoaded(true)} className="h-full w-full object-cover" />
            </m.div>
        </LazyMotion>
    );
};

export default FadeInImage;

