/**
 * Kit Builder Components
 * 
 * Components for configuring Smart Kits with customizable slots.
 * Supports "Swap" (single-select) and "Add More" (multi-select) interactions.
 * 
 * Uses unified types from @/types/commerce
 */
'use client'

import { useState, useEffect } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { X, Check, Plus, ChevronRight } from 'lucide-react'
import { Product } from '@/services/products/types'
import { ResolvedKit, ResolvedKitSlot } from '@/types/commerce'
import { cn } from '@/lib/utils'

// ============================================================================
// Types
// ============================================================================

export interface KitSelections {
  [slotName: string]: string[] // Array of selected product IDs per slot
}

export interface KitBuilderProps {
  resolvedKit: ResolvedKit
  initialSelections?: KitSelections
  onChange?: (selections: KitSelections) => void
  lng?: string
  className?: string
}

export interface KitSlotRowProps {
  slot: ResolvedKitSlot
  selectedIds: string[]
  onEdit: () => void
  lng?: string
}

export interface KitSelectionModalProps {
  isOpen: boolean
  onClose: () => void
  slot: ResolvedKitSlot
  selectedIds: string[]
  onConfirm: (selectedIds: string[]) => void
  lng?: string
}

// ============================================================================
// Kit Selection Modal
// ============================================================================

export function KitSelectionModal({
  isOpen,
  onClose,
  slot,
  selectedIds,
  onConfirm,
  lng = 'en',
}: KitSelectionModalProps) {
  const [localSelection, setLocalSelection] = useState<string[]>(selectedIds)

  // Reset local selection when modal opens
  useEffect(() => {
    if (isOpen) {
      setLocalSelection(selectedIds)
    }
  }, [isOpen, selectedIds])

  const handleToggleItem = (itemId: string) => {
    if (slot.allowMultiple) {
      // Multi-select: toggle item
      setLocalSelection((prev) =>
        prev.includes(itemId)
          ? prev.filter((id) => id !== itemId)
          : [...prev, itemId]
      )
    } else {
      // Single-select: replace selection
      setLocalSelection([itemId])
    }
  }

  const handleConfirm = () => {
    onConfirm(localSelection)
    onClose()
  }

  const isSelected = (itemId: string) => localSelection.includes(itemId)
  const isDefault = (itemId: string) => slot.defaultItems.some(i => i.product_id === itemId)

  return (
    <AnimatePresence>
      {isOpen && (
        <>
          {/* Backdrop */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50"
            onClick={onClose}
          />

          {/* Modal */}
          <motion.div
            initial={{ opacity: 0, scale: 0.95, y: 20 }}
            animate={{ opacity: 1, scale: 1, y: 0 }}
            exit={{ opacity: 0, scale: 0.95, y: 20 }}
            transition={{ type: 'spring', damping: 25, stiffness: 300 }}
            className="fixed inset-4 md:inset-auto md:left-1/2 md:top-1/2 md:-translate-x-1/2 md:-translate-y-1/2 md:w-full md:max-w-lg md:max-h-[80vh] bg-zinc-900 border border-zinc-700 rounded-xl shadow-2xl z-50 flex flex-col overflow-hidden"
          >
            {/* Header */}
            <div className="flex items-center justify-between p-4 border-b border-zinc-800">
              <div>
                <h3 className="text-lg font-semibold text-white">
                  {lng === 'fr' ? 'Configurer' : 'Configure'} {slot.slotName}
                </h3>
                <p className="text-sm text-zinc-400">
                  {slot.allowMultiple
                    ? lng === 'fr'
                      ? 'S├⌐lectionnez autant que vous le souhaitez'
                      : 'Select as many as you need'
                    : lng === 'fr'
                    ? 'Choisissez une option'
                    : 'Choose one option'}
                </p>
              </div>
              <button
                onClick={onClose}
                className="p-2 text-zinc-400 hover:text-white transition-colors"
              >
                <X className="w-5 h-5" />
              </button>
            </div>

            {/* Item List */}
            <div className="flex-1 overflow-y-auto p-4 space-y-2">
              {slot.availableOptions.map((item) => (
                <button
                  key={item.id}
                  onClick={() => handleToggleItem(item.id)}
                  className={cn(
                    'w-full flex items-center gap-4 p-3 rounded-lg border transition-all',
                    isSelected(item.id)
                      ? 'border-amber-500/50 bg-amber-500/10'
                      : 'border-zinc-700 bg-zinc-800/50 hover:border-zinc-600'
                  )}
                >
                  {/* Selection indicator */}
                  <div
                    className={cn(
                      'w-5 h-5 rounded-full border-2 flex items-center justify-center flex-shrink-0',
                      isSelected(item.id)
                        ? 'border-amber-500 bg-amber-500'
                        : 'border-zinc-600'
                    )}
                  >
                    {isSelected(item.id) && <Check className="w-3 h-3 text-black" />}
                  </div>

                  {/* Item thumbnail */}
                  <div className="w-12 h-12 rounded bg-zinc-700 flex-shrink-0 overflow-hidden">
                    {item.imageUrl ? (
                      <img
                        src={item.imageUrl}
                        alt={item.name}
                        className="w-full h-full object-cover"
                      />
                    ) : (
                      <div className="w-full h-full flex items-center justify-center text-zinc-500 text-xs">
                        IMG
                      </div>
                    )}
                  </div>

                  {/* Item info */}
                  <div className="flex-1 text-left">
                    <p className="font-medium text-white">{item.name}</p>
                    {isDefault(item.id) && (
                      <span className="text-xs text-amber-400">
                        {lng === 'fr' ? 'Inclus par d├⌐faut' : 'Included by default'}
                      </span>
                    )}
                  </div>

                  {/* BLIND QUOTE: No prices shown */}
                </button>
              ))}
            </div>

            {/* Footer */}
            <div className="p-4 border-t border-zinc-800 flex gap-3">
              <button
                onClick={onClose}
                className="flex-1 px-4 py-2 rounded-lg border border-zinc-700 text-zinc-300 hover:bg-zinc-800 transition-colors"
              >
                {lng === 'fr' ? 'Annuler' : 'Cancel'}
              </button>
              <button
                onClick={handleConfirm}
                className="flex-1 px-4 py-2 rounded-lg bg-amber-500 text-black font-semibold hover:bg-amber-400 transition-colors"
              >
                {lng === 'fr' ? 'Confirmer' : 'Confirm'} ({localSelection.length})
              </button>
            </div>
          </motion.div>
        </>
      )}
    </AnimatePresence>
  )
}

// ============================================================================
// Kit Slot Row
// ============================================================================

export function KitSlotRow({ slot, selectedIds, onEdit, lng = 'en' }: KitSlotRowProps) {
  const selectedItems = slot.availableOptions.filter((item) =>
    selectedIds.includes(item.id)
  )

  return (
    <div className="flex items-center justify-between p-4 bg-zinc-800/50 rounded-lg border border-zinc-700">
      <div className="flex-1">
        <h4 className="font-medium text-white">{slot.slotName}</h4>
        <div className="mt-1 flex flex-wrap gap-1">
          {selectedItems.length > 0 ? (
            selectedItems.map((item) => (
              <span
                key={item.id}
                className="inline-flex items-center px-2 py-0.5 rounded-full text-xs bg-zinc-700 text-zinc-300"
              >
                {item.name}
              </span>
            ))
          ) : (
            <span className="text-sm text-zinc-500">
              {lng === 'fr' ? 'Aucune s├⌐lection' : 'No selection'}
            </span>
          )}
        </div>
      </div>

      <button
        onClick={onEdit}
        className="flex items-center gap-2 px-3 py-2 rounded-lg text-sm font-medium text-amber-400 hover:bg-amber-500/10 transition-colors"
      >
        {slot.allowMultiple ? (
          <>
            <Plus className="w-4 h-4" />
            {lng === 'fr' ? 'Ajouter' : 'Add More'}
          </>
        ) : (
          <>
            <ChevronRight className="w-4 h-4" />
            {lng === 'fr' ? 'Modifier' : 'Edit'}
          </>
        )}
      </button>
    </div>
  )
}

// ============================================================================
// Kit Builder (Main Component)
// ============================================================================

export function KitBuilder({
  resolvedKit,
  initialSelections,
  onChange,
  lng = 'en',
  className,
}: KitBuilderProps) {
  // Initialize selections from defaults if not provided
  const [selections, setSelections] = useState<KitSelections>(() => {
    if (initialSelections) return initialSelections

    const defaults: KitSelections = {}
    for (const slot of resolvedKit.slots) {
      defaults[slot.slotName] = slot.defaultItems.map(i => i.product_id)
    }
    return defaults
  })

  const [activeSlotName, setActiveSlotName] = useState<string | null>(null)

  const activeSlot = resolvedKit.slots.find(
    (s) => s.slotName === activeSlotName
  )

  const handleSlotConfirm = (slotName: string, selectedIds: string[]) => {
    const newSelections = { ...selections, [slotName]: selectedIds }
    setSelections(newSelections)
    onChange?.(newSelections)
  }

  return (
    <div className={cn('space-y-4', className)}>
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold text-white">
          {lng === 'fr' ? 'Configurer votre kit' : 'Configure Your Kit'}
        </h3>
      </div>

      <div className="space-y-3">
        {resolvedKit.slots.map((slot) => (
          <KitSlotRow
            key={slot.slotName}
            slot={slot}
            selectedIds={selections[slot.slotName] || []}
            onEdit={() => setActiveSlotName(slot.slotName)}
            lng={lng}
          />
        ))}
      </div>

      {/* Selection Modal */}
      {activeSlot && (
        <KitSelectionModal
          isOpen={!!activeSlotName}
          onClose={() => setActiveSlotName(null)}
          slot={activeSlot}
          selectedIds={selections[activeSlot.slotName] || []}
          onConfirm={(ids) => handleSlotConfirm(activeSlot.slotName, ids)}
          lng={lng}
        />
      )}
    </div>
  )
}

export default KitBuilder

/**
 * DateRangePicker Component
 * 
 * A styled date range picker for rental period selection.
 * Uses native HTML date inputs for reliability.
 * 
 * Design Archetype: Dark Cinema
 */
'use client'

import { useState, useEffect } from 'react'
import { Calendar, ArrowRight } from 'lucide-react'
import { format, addDays, differenceInDays, parseISO, isValid } from 'date-fns'
import { cn } from '@/lib/utils'

export interface DateRange {
  start: string // ISO date string (YYYY-MM-DD)
  end: string   // ISO date string (YYYY-MM-DD)
}

interface DateRangePickerProps {
  value?: DateRange | null
  onChange: (range: DateRange | null) => void
  minDate?: string
  maxDays?: number
  className?: string
  error?: string
}

export function DateRangePicker({
  value,
  onChange,
  minDate,
  maxDays = 365,
  className,
  error,
}: DateRangePickerProps) {
  const today = new Date().toISOString().split('T')[0]
  const minDateValue = minDate || today

  const [startDate, setStartDate] = useState(value?.start || '')
  const [endDate, setEndDate] = useState(value?.end || '')

  // Sync internal state with external value
  useEffect(() => {
    if (value) {
      setStartDate(value.start)
      setEndDate(value.end)
    }
  }, [value])

  // Calculate rental days
  const rentalDays = startDate && endDate
    ? differenceInDays(parseISO(endDate), parseISO(startDate)) + 1
    : 0

  // Handle start date change
  const handleStartChange = (newStart: string) => {
    setStartDate(newStart)
    
    // If end date is before start, reset it
    if (endDate && newStart > endDate) {
      const newEnd = newStart
      setEndDate(newEnd)
      onChange({ start: newStart, end: newEnd })
    } else if (endDate) {
      onChange({ start: newStart, end: endDate })
    } else {
      // Auto-set end date to same day if not set
      setEndDate(newStart)
      onChange({ start: newStart, end: newStart })
    }
  }

  // Handle end date change
  const handleEndChange = (newEnd: string) => {
    setEndDate(newEnd)
    if (startDate) {
      onChange({ start: startDate, end: newEnd })
    }
  }

  // Quick select options
  const quickSelects = [
    { label: '1 Day', days: 1 },
    { label: '3 Days', days: 3 },
    { label: '1 Week', days: 7 },
    { label: '2 Weeks', days: 14 },
  ]

  const handleQuickSelect = (days: number) => {
    const start = startDate || today
    const end = format(addDays(parseISO(start), days - 1), 'yyyy-MM-dd')
    setStartDate(start)
    setEndDate(end)
    onChange({ start, end })
  }

  return (
    <div className={cn('space-y-4', className)}>
      {/* Date Inputs */}
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
        {/* Start Date */}
        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-2">
            <Calendar className="w-4 h-4 inline mr-1.5" />
            Pickup Date *
          </label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => handleStartChange(e.target.value)}
            min={minDateValue}
            className={cn(
              'w-full px-4 py-3 bg-zinc-800 border rounded-lg text-white',
              'focus:outline-none focus:ring-2 focus:ring-white/20',
              'appearance-none cursor-pointer',
              '[&::-webkit-calendar-picker-indicator]:filter',
              '[&::-webkit-calendar-picker-indicator]:invert',
              error ? 'border-red-500' : 'border-zinc-700'
            )}
          />
        </div>

        {/* End Date */}
        <div>
          <label className="block text-sm font-medium text-zinc-300 mb-2">
            <Calendar className="w-4 h-4 inline mr-1.5" />
            Return Date *
          </label>
          <input
            type="date"
            value={endDate}
            onChange={(e) => handleEndChange(e.target.value)}
            min={startDate || minDateValue}
            className={cn(
              'w-full px-4 py-3 bg-zinc-800 border rounded-lg text-white',
              'focus:outline-none focus:ring-2 focus:ring-white/20',
              'appearance-none cursor-pointer',
              '[&::-webkit-calendar-picker-indicator]:filter',
              '[&::-webkit-calendar-picker-indicator]:invert',
              error ? 'border-red-500' : 'border-zinc-700'
            )}
          />
        </div>
      </div>

      {/* Error Message */}
      {error && (
        <p className="text-sm text-red-400">{error}</p>
      )}

      {/* Quick Select Buttons */}
      <div className="flex flex-wrap gap-2">
        <span className="text-xs text-zinc-500 self-center mr-2">Quick select:</span>
        {quickSelects.map((option) => (
          <button
            key={option.days}
            type="button"
            onClick={() => handleQuickSelect(option.days)}
            className={cn(
              'px-3 py-1.5 text-xs font-medium rounded-full transition-colors',
              rentalDays === option.days
                ? 'bg-white text-black'
                : 'bg-zinc-800 text-zinc-400 hover:bg-zinc-700 hover:text-white border border-zinc-700'
            )}
          >
            {option.label}
          </button>
        ))}
      </div>

      {/* Duration Display */}
      {rentalDays > 0 && (
        <div className="flex items-center gap-3 p-4 bg-zinc-800/50 border border-zinc-700 rounded-lg">
          <div className="flex items-center gap-2 text-sm text-zinc-300">
            <span className="font-medium">
              {startDate && format(parseISO(startDate), 'MMM d, yyyy')}
            </span>
            <ArrowRight className="w-4 h-4 text-zinc-500" />
            <span className="font-medium">
              {endDate && format(parseISO(endDate), 'MMM d, yyyy')}
            </span>
          </div>
          <div className="ml-auto">
            <span className="px-3 py-1 bg-white/10 text-white text-sm font-semibold rounded-full">
              {rentalDays} {rentalDays === 1 ? 'day' : 'days'}
            </span>
          </div>
        </div>
      )}
    </div>
  )
}

/**
 * Calculate rental days from a date range
 */
export function calculateRentalDays(range: DateRange | null): number {
  if (!range || !range.start || !range.end) return 0
  try {
    const start = parseISO(range.start)
    const end = parseISO(range.end)
    if (!isValid(start) || !isValid(end)) return 0
    return Math.max(1, differenceInDays(end, start) + 1)
  } catch {
    return 0
  }
}

/**
 * Format a date range for display
 */
export function formatDateRange(range: DateRange | null): string {
  if (!range || !range.start || !range.end) return 'Not selected'
  try {
    const start = parseISO(range.start)
    const end = parseISO(range.end)
    if (!isValid(start) || !isValid(end)) return 'Invalid dates'
    return `${format(start, 'MMM d')} - ${format(end, 'MMM d, yyyy')}`
  } catch {
    return 'Invalid dates'
  }
}

"use client";
import { useMotionValue } from "framer-motion";
import React, { useState, useEffect } from "react";
import { useMotionTemplate, motion } from "framer-motion";
import { cn } from "@/lib/utils";

export const EvervaultCard = ({
    text,
    className,
    children,
}: {
    text?: string;
    className?: string;
    children?: React.ReactNode;
}) => {
    const mouseX = useMotionValue(0);
    const mouseY = useMotionValue(0);

    const [randomString, setRandomString] = useState("");

    useEffect(() => {
        const str = generateRandomString(1500);
        setRandomString(str);
    }, []);

    function onMouseMove({ currentTarget, clientX, clientY }: any) {
        const { left, top } = currentTarget.getBoundingClientRect();
        mouseX.set(clientX - left);
        mouseY.set(clientY - top);

        const str = generateRandomString(1500);
        setRandomString(str);
    }

    return (
        <div
            className={cn(
                "p-0.5 bg-transparent flex items-center justify-center w-full h-full relative",
                className
            )}
        >
            <div
                onMouseMove={onMouseMove}
                className="group/card rounded-3xl w-full relative overflow-hidden bg-transparent flex items-center justify-center h-full"
            >
                <CardPattern
                    mouseX={mouseX}
                    mouseY={mouseY}
                    randomString={randomString}
                />
                <div className="relative z-10 flex items-center justify-center">
                    <div className="relative h-44 w-44 rounded-full flex items-center justify-center text-white font-bold text-4xl">
                        {/* Removed the blur-sm text container slightly to allow children (like images) to be seen if needed, 
                but keeping text logic for now. Modified to accept children. */}
                        <div className="absolute w-full h-full bg-black/[0.8] blur-sm rounded-full opacity-0 group-hover/card:opacity-100 transition-opacity duration-500" />
                        {text && <span className="dark:text-white text-black z-20 opacity-0 group-hover/card:opacity-100 transition-opacity duration-500">{text}</span>}
                        {children}
                    </div>
                </div>
            </div>
        </div>
    );
};

export function CardPattern({ mouseX, mouseY, randomString }: any) {
    const maskImage = useMotionTemplate`radial-gradient(250px at ${mouseX}px ${mouseY}px, white, transparent)`;
    const style = { maskImage, WebkitMaskImage: maskImage };

    return (
        <div className="pointer-events-none absolute inset-0">
            <div className="absolute inset-0 rounded-2xl [mask-image:linear-gradient(white,transparent)] group-hover/card:opacity-50"></div>
            <motion.div
                className="absolute inset-0 rounded-2xl bg-gradient-to-r from-green-500 to-blue-700 opacity-0 group-hover/card:opacity-100 backdrop-blur-xl transition duration-500"
                style={style}
            />
            <motion.div
                className="absolute inset-0 rounded-2xl opacity-0 mix-blend-overlay group-hover/card:opacity-100"
                style={style}
            >
                <p className="absolute inset-x-0 text-xs h-full break-words whitespace-pre-wrap text-white font-mono font-bold transition duration-500">
                    {randomString}
                </p>
            </motion.div>
        </div>
    );
}

const characters =
    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
export const generateRandomString = (length: number) => {
    let result = "";
    for (let i = 0; i < length; i++) {
        result += characters.charAt(Math.floor(Math.random() * characters.length));
    }
    return result;
};

export const Icon = ({ className, ...rest }: any) => {
    return (
        <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth="1.5"
            stroke="currentColor"
            className={className}
            {...rest}
        >
            <path strokeLinecap="round" strokeLinejoin="round" d="M12 6v12m6-6H6" />
        </svg>
    );
};

"use client";
import { useEffect, useRef, useState } from "react";
import { Color, Scene, Fog, PerspectiveCamera, Vector3, Group } from "three";
import ThreeGlobe from "three-globe";
import { useThree, Canvas, extend } from "@react-three/fiber";
import { OrbitControls } from "@react-three/drei";
import countries from "@/data/globe.json";
declare module "@react-three/fiber" {
    interface ThreeElements {
        threeGlobe: ThreeElements["mesh"] & {
            new(): ThreeGlobe;
        };
    }
}

extend({ ThreeGlobe: ThreeGlobe });

const RING_PROPAGATION_SPEED = 3;
const aspect = 1.2;
const cameraZ = 300;

type Position = {
    order: number;
    startLat: number;
    startLng: number;
    endLat: number;
    endLng: number;
    arcAlt: number;
    color: string;
};

export type GlobeConfig = {
    pointSize?: number;
    globeColor?: string;
    showAtmosphere?: boolean;
    atmosphereColor?: string;
    atmosphereAltitude?: number;
    emissive?: string;
    emissiveIntensity?: number;
    shininess?: number;
    polygonColor?: string;
    ambientLight?: string;
    directionalLeftLight?: string;
    directionalTopLight?: string;
    pointLight?: string;
    arcTime?: number;
    arcLength?: number;
    rings?: number;
    maxRings?: number;
    initialPosition?: {
        lat: number;
        lng: number;
    };
    autoRotate?: boolean;
    autoRotateSpeed?: number;
};

interface WorldProps {
    globeConfig: GlobeConfig;
    data: Position[];
}

const numbersOfRings = [0];

export function Globe({ globeConfig, data }: WorldProps) {
    const globeRef = useRef<ThreeGlobe | null>(null);
    const groupRef = useRef<Group>(null);
    const [isInitialized, setIsInitialized] = useState(false);

    const defaultProps = {
        pointSize: 1,
        atmosphereColor: "#ffffff",
        showAtmosphere: true,
        atmosphereAltitude: 0.1,
        polygonColor: "rgba(255,255,255,0.7)",
        globeColor: "#1d072e",
        emissive: "#000000",
        emissiveIntensity: 0.1,
        shininess: 0.9,
        arcTime: 2000,
        arcLength: 0.9,
        rings: 1,
        maxRings: 3,
        ...globeConfig,
    };

    // Initialize globe only once
    useEffect(() => {
        if (!globeRef.current && groupRef.current) {
            globeRef.current = new ThreeGlobe();
            (groupRef.current as any).add(globeRef.current);
            setIsInitialized(true);
        }
    }, []);

    // Build material when globe is initialized or when relevant props change
    useEffect(() => {
        if (!globeRef.current || !isInitialized) return;

        const globeMaterial = globeRef.current.globeMaterial() as unknown as {
            color: Color;
            emissive: Color;
            emissiveIntensity: number;
            shininess: number;
        };
        globeMaterial.color = new Color(globeConfig.globeColor);
        globeMaterial.emissive = new Color(globeConfig.emissive);
        globeMaterial.emissiveIntensity = globeConfig.emissiveIntensity || 0.1;
        globeMaterial.shininess = globeConfig.shininess || 0.9;
    }, [
        isInitialized,
        globeConfig.globeColor,
        globeConfig.emissive,
        globeConfig.emissiveIntensity,
        globeConfig.shininess,
    ]);

    // Build data when globe is initialized or when data changes
    useEffect(() => {
        if (!globeRef.current || !isInitialized || !data) return;

        const arcs = data;
        const points = [];
        for (let i = 0; i < arcs.length; i++) {
            const arc = arcs[i];
            const rgb = hexToRgb(arc.color) as { r: number; g: number; b: number };
            points.push({
                size: defaultProps.pointSize,
                order: arc.order,
                color: arc.color,
                lat: arc.startLat,
                lng: arc.startLng,
            });
            points.push({
                size: defaultProps.pointSize,
                order: arc.order,
                color: arc.color,
                lat: arc.endLat,
                lng: arc.endLng,
            });
        }

        // remove duplicates for same lat and lng
        const filteredPoints = points.filter(
            (v, i, a) =>
                a.findIndex((v2) =>
                    ["lat", "lng"].every(
                        (k) => v2[k as "lat" | "lng"] === v[k as "lat" | "lng"],
                    ),
                ) === i,
        );

        globeRef.current
            .hexPolygonsData(countries.features)
            .hexPolygonResolution(3)
            .hexPolygonMargin(0.7)
            .showAtmosphere(defaultProps.showAtmosphere)
            .atmosphereColor(defaultProps.atmosphereColor)
            .atmosphereAltitude(defaultProps.atmosphereAltitude)
            .hexPolygonColor(() => defaultProps.polygonColor);

        globeRef.current
            .arcsData(data)
            .arcStartLat((d) => (d as { startLat: number }).startLat * 1)
            .arcStartLng((d) => (d as { startLng: number }).startLng * 1)
            .arcEndLat((d) => (d as { endLat: number }).endLat * 1)
            .arcEndLng((d) => (d as { endLng: number }).endLng * 1)
            .arcColor((e: any) => (e as { color: string }).color)
            .arcAltitude((e) => (e as { arcAlt: number }).arcAlt * 1)
            .arcStroke(() => [0.32, 0.28, 0.3][Math.round(Math.random() * 2)])
            .arcDashLength(defaultProps.arcLength)
            .arcDashInitialGap((e) => (e as { order: number }).order * 1)
            .arcDashGap(15)
            .arcDashAnimateTime(() => defaultProps.arcTime);

        globeRef.current
            .pointsData(filteredPoints)
            .pointColor((e) => (e as { color: string }).color)
            .pointsMerge(true)
            .pointAltitude(0.0)
            .pointRadius(2);

        globeRef.current
            .ringsData([])
            .ringColor(() => defaultProps.polygonColor)
            .ringMaxRadius(defaultProps.maxRings)
            .ringPropagationSpeed(RING_PROPAGATION_SPEED)
            .ringRepeatPeriod(
                (defaultProps.arcTime * defaultProps.arcLength) / defaultProps.rings,
            );
    }, [
        isInitialized,
        data,
        defaultProps.pointSize,
        defaultProps.showAtmosphere,
        defaultProps.atmosphereColor,
        defaultProps.atmosphereAltitude,
        defaultProps.polygonColor,
        defaultProps.arcLength,
        defaultProps.arcTime,
        defaultProps.rings,
        defaultProps.maxRings,
    ]);

    // Handle rings animation with cleanup
    useEffect(() => {
        if (!globeRef.current || !isInitialized || !data) return;

        const interval = setInterval(() => {
            if (!globeRef.current) return;

            const newNumbersOfRings = genRandomNumbers(
                0,
                data.length,
                Math.floor((data.length * 4) / 5),
            );

            const ringsData = data
                .filter((d, i) => newNumbersOfRings.includes(i))
                .map((d) => ({
                    lat: d.startLat,
                    lng: d.startLng,
                    color: d.color,
                }));

            globeRef.current.ringsData(ringsData);
        }, 2000);

        return () => {
            clearInterval(interval);
        };
    }, [isInitialized, data]);

    return <group ref={groupRef} />;
}

export function WebGLRendererConfig() {
    const { gl, size } = useThree();

    useEffect(() => {
        gl.setPixelRatio(window.devicePixelRatio);
        gl.setSize(size.width, size.height);
        gl.setClearColor(0xffaaff, 0);
    }, []);

    return null;
}

export function World(props: WorldProps) {
    const { globeConfig } = props;
    const scene = new Scene();
    scene.fog = new Fog(0xffffff, 400, 2000);
    return (
        <Canvas scene={scene} camera={new PerspectiveCamera(50, aspect, 180, 1800)}>
            <WebGLRendererConfig />
            <ambientLight color={globeConfig.ambientLight} intensity={0.6} />
            <directionalLight
                color={globeConfig.directionalLeftLight}
                position={new Vector3(-400, 100, 400)}
            />
            <directionalLight
                color={globeConfig.directionalTopLight}
                position={new Vector3(-200, 500, 200)}
            />
            <pointLight
                color={globeConfig.pointLight}
                position={new Vector3(-200, 500, 200)}
                intensity={0.8}
            />
            <Globe {...props} />
            <OrbitControls
                enablePan={false}
                enableZoom={false}
                minDistance={cameraZ}
                maxDistance={cameraZ}
                autoRotateSpeed={1}
                autoRotate={true}
                minPolarAngle={Math.PI / 3.5}
                maxPolarAngle={Math.PI - Math.PI / 3}
            />
        </Canvas>
    );
}

export function hexToRgb(hex: string) {
    const shorthandRegex = /^#?([a-f\d])([a-f\d])([a-f\d])$/i;
    hex = hex.replace(shorthandRegex, function (m, r, g, b) {
        return r + r + g + g + b + b;
    });

    const result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
    return result
        ? {
            r: parseInt(result[1], 16),
            g: parseInt(result[2], 16),
            b: parseInt(result[3], 16),
        }
        : null;
}

export function genRandomNumbers(min: number, max: number, count: number) {
    const arr = [];
    while (arr.length < count) {
        const r = Math.floor(Math.random() * (max - min)) + min;
        if (arr.indexOf(r) === -1) arr.push(r);
    }

    return arr;
}

"use client";

import { cn } from "@/lib/utils";
import React, { useEffect, useState } from "react";
import Image from "next/image";
import Link from "next/link";
import { ArrowUpRight } from "lucide-react";

export const InfiniteMovingCards = ({
    items,
    direction = "left",
    speed = "fast",
    pauseOnHover = true,
    className,
}: {
    items: {
        quote: string; // Used as Title for News
        name: string;  // Used as Date/Category
        title: string; // Used as Link Text
        href?: string;
        image?: string;
    }[];
    direction?: "left" | "right";
    speed?: "fast" | "normal" | "slow";
    pauseOnHover?: boolean;
    className?: string;
}) => {
    const containerRef = React.useRef<HTMLDivElement>(null);
    const scrollerRef = React.useRef<HTMLUListElement>(null);

    useEffect(() => {
        addAnimation();
    }, []);
    const [start, setStart] = useState(false);
    function addAnimation() {
        if (containerRef.current && scrollerRef.current) {
            const scrollerContent = Array.from(scrollerRef.current.children);

            scrollerContent.forEach((item) => {
                const duplicatedItem = item.cloneNode(true);
                if (scrollerRef.current) {
                    scrollerRef.current.appendChild(duplicatedItem);
                }
            });

            getDirection();
            getSpeed();
            setStart(true);
        }
    }
    const getDirection = () => {
        if (containerRef.current) {
            if (direction === "left") {
                containerRef.current.style.setProperty(
                    "--animation-direction",
                    "forwards",
                );
            } else {
                containerRef.current.style.setProperty(
                    "--animation-direction",
                    "reverse",
                );
            }
        }
    };
    const getSpeed = () => {
        if (containerRef.current) {
            if (speed === "fast") {
                containerRef.current.style.setProperty("--animation-duration", "20s");
            } else if (speed === "normal") {
                containerRef.current.style.setProperty("--animation-duration", "40s");
            } else {
                containerRef.current.style.setProperty("--animation-duration", "80s");
            }
        }
    };
    return (
        <div
            ref={containerRef}
            className={cn(
                "scroller relative z-20 max-w-7xl overflow-hidden [mask-image:linear-gradient(to_right,transparent,white_10%,white_90%,transparent)]",
                className,
            )}
        >
            <ul
                ref={scrollerRef}
                className={cn(
                    "flex w-max min-w-full shrink-0 flex-nowrap gap-6 py-4",
                    start && "animate-scroll",
                    pauseOnHover && "hover:[animation-play-state:paused]",
                )}
            >
                {items.map((item, idx) => (
                    <li
                        className="w-[350px] max-w-full relative rounded-2xl border border-white/10 bg-zinc-950/50 flex-shrink-0 px-8 py-6 md:w-[450px] transition-colors hover:border-red-600/30 hover:bg-zinc-900 group"
                        key={idx}
                    >
                        {/* Content */}
                        <div className="relative z-20 flex flex-col h-full justify-between">
                            <div>
                                {item.image && (
                                    <div className="relative h-48 w-full mb-6 overflow-hidden rounded-lg">
                                        <Image
                                            src={item.image}
                                            alt={item.quote}
                                            fill
                                            className="object-cover transition-transform duration-500 group-hover:scale-105"
                                        />
                                        {/* Overlay for better text readability if we were putting text over it, but here it's separate. 
                                Let's add a subtle border inner.
                             */}
                                        <div className="absolute inset-0 border-inset border border-white/5 rounded-lg pointer-events-none" />
                                    </div>
                                )}

                                <div className="flex items-center gap-2 mb-3">
                                    <span className="text-xs font-mono uppercase text-[#D00000] tracking-widest">{item.name}</span>
                                </div>

                                <h3 className="text-xl font-display font-bold text-white uppercase leading-tight mb-4 min-h-[3rem]">
                                    {item.quote}
                                </h3>
                            </div>

                            <div className="mt-4 flex items-center justify-between border-t border-white/5 pt-4">
                                <span className="text-sm text-zinc-500 font-light flex items-center gap-2 group-hover:text-white transition-colors">
                                    Read Article <ArrowUpRight className="w-3 h-3" />
                                </span>
                            </div>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

