import { verifyAdminAccess } from '@/services/auth/access-control';
import { createServerClient } from '@/lib/pocketbase/server';
import { getQuoteService } from '@/services';
import type { Quote, QuoteStatus } from '@/services';

export type { Quote, QuoteStatus }; // Re-export for component consumption if needed (or prefer importing from services)
export type RentalRequest = Quote; // Alias for backward compatibility if needed

export interface GetRequestsResponse {
    items: RentalRequest[];
    totalItems: number;
    totalPages: number;
    page: number;
    limit: number;
}

export async function getAdminRentalRequests(
    page: number = 1,
    limit: number = 20,
    status?: QuoteStatus
): Promise<GetRequestsResponse> {
    // 1. Security Check
    const canAccess = await verifyAdminAccess();
    if (!canAccess) {
        throw new Error('Unauthorized Access');
    }

    // 2. Fetch Data
    const pb = await createServerClient(false);
    const service = getQuoteService(pb);

    // Use Service Layer (handles mapping, json parsing, expansion)
    const result = await service.getQuotes(page, limit, status);

    return {
        items: result.items,
        totalItems: result.totalItems,
        totalPages: result.totalPages,
        page: result.page,
        limit: result.perPage
    };
}


export async function getRequestDetails(id: string): Promise<RentalRequest | null> {
    const canAccess = await verifyAdminAccess();
    if (!canAccess) return null;

    const pb = await createServerClient(false);
    const service = getQuoteService(pb);

    try {
        const quote = await service.getQuoteById(id);
        return quote;
    } catch (error) {
        console.error('Error fetching quote details:', error);
        return null;
    }
}

export async function updateQuotePricing(
    formData: FormData
): Promise<{ success: boolean; error?: string }> {
    const canAccess = await verifyAdminAccess();
    if (!canAccess) return { success: false, error: 'Unauthorized' };

    const id = formData.get('id') as string;
    const price = Number(formData.get('price'));
    const file = formData.get('file') as File;
    const sendEmail = formData.get('sendEmail') !== 'false'; // Default to true

    if (!id || !price || !file) {
        return { success: false, error: 'Missing required fields' };
    }

    const pb = await createServerClient(true);
    const service = getQuoteService(pb);

    try {
        // Get quote details before update (for email)
        const quoteBeforeUpdate = await service.getQuoteById(id);
        if (!quoteBeforeUpdate) {
            return { success: false, error: 'Quote not found' };
        }

        // Upload PDF and update quote
        const result = await service.uploadQuote(id, file, price);
        if (!result.success) {
            return { success: false, error: result.error };
        }

        // Get updated quote (now has access_token if it was set during create)
        const quoteRecord = await pb.collection('quotes').getOne(id);

        // Send email notification if enabled
        if (sendEmail && quoteRecord.access_token) {
            try {
                const { getEmailService } = await import('@/services');
                const emailService = getEmailService();

                await emailService.sendQuoteReadyNotification({
                    to: quoteBeforeUpdate.clientEmail,
                    customerName: quoteBeforeUpdate.clientName,
                    confirmationNumber: quoteBeforeUpdate.confirmationNumber || id,
                    quoteId: id,
                    accessToken: quoteRecord.access_token,
                    estimatedPrice: price,
                    language: quoteBeforeUpdate.language || 'en',
                });

                console.log(`≡ƒôº Quote ready email sent to ${quoteBeforeUpdate.clientEmail}`);
            } catch (emailError) {
                // Log but don't fail the action if email fails
                console.error('Failed to send quote ready email:', emailError);
            }
        }

        return { success: true };
    } catch (error: any) {
        console.error('Error updating quote pricing:', error);
        return { success: false, error: error.message || 'Failed to update quote' };
    }
}

'use server'

import { createServerClient } from "@/lib/pocketbase/server"
import { revalidatePath } from "next/cache"
import { slugify } from "@/lib/utils/slugify"

export type BlogActionResult = {
    success: boolean
    error?: string
    data?: any
}

export async function createPost(formData: FormData): Promise<BlogActionResult> {
    try {
        const pb = await createServerClient()

        // Basic validation
        const title = formData.get('title')
        const content = formData.get('content')

        if (!title) {
            return { success: false, error: "Title is required" }
        }

        // Ensure slug exists
        let slug = formData.get('slug')?.toString()
        if (!slug) {
            slug = slugify(title.toString())
        }

        // Construct clean data object
        const data: Record<string, any> = {
            title,
            slug,
            content: content || '',
            excerpt: formData.get('excerpt') || '',
            published: formData.get('published') === 'true',
        }

        // Handle cover image separately
        const coverImage = formData.get('cover_image')
        if (coverImage && coverImage instanceof File && coverImage.size > 0) {
            data.cover_image = coverImage
        }

        console.log('[BlogAction] Creating post:', { ...data, cover_image: data.cover_image ? 'File present' : 'No file' })

        // Create record
        const record = await pb.collection('posts').create(data)

        // Revalidate admin dashboard and blog pages
        revalidatePath('/[lng]/admin/admin/blog', 'page')
        revalidatePath('/[lng]/blog', 'layout')

        return { success: true, data: record }

    } catch (error: any) {
        console.error('[BlogAction] Error creating post:', error)

        let errorMessage = "Failed to create post"
        if (error?.response?.data) {
            // Format detailed validation errors
            const details = Object.entries(error.response.data)
                .map(([key, val]: [string, any]) => `${key}: ${val.message}`)
                .join(', ')
            if (details) errorMessage += `: ${details}`
        } else if (error?.message) {
            errorMessage += `: ${error.message}`
        }

        return { success: false, error: errorMessage }
    }
}

