export async function mergeGuestCart(guestItems: CartItem[]): Promise<{ success: boolean; error?: string }> {
  try {
    const pb = await createServerClient();

    if (!pb.authStore.isValid) {
      return { success: false, error: "Not authenticated" };
    }

    const userId = pb.authStore.model?.id;
    if (!userId) {
      return { success: false, error: "Invalid user session" };
    }

    // Get existing user cart items
    const existingCartItems = await pb.collection('cart_items').getFullList({
      filter: `user="${userId}"`,
    });

    // Create a map of existing items by product ID for quick lookup
    const existingItemsMap = new Map<string, any>();
    existingCartItems.forEach(item => {
      existingItemsMap.set(item.product, item);
    });

    // Process each guest item
    for (const guestItem of guestItems) {
      const existingItem = existingItemsMap.get(guestItem.product.id);

      if (existingItem) {
        // Update quantity if item already exists
        const newQuantity = existingItem.quantity + guestItem.quantity;
        await pb.collection('cart_items').update(existingItem.id, {
          quantity: newQuantity,
          // Update kit selections if they differ (take the more recent one)
          kit_selections: guestItem.kitSelections ? JSON.stringify(guestItem.kitSelections) : existingItem.kit_selections,
        });
      } else {
        // Add new item to cart
        await pb.collection('cart_items').create({
          user: userId,
          product: guestItem.product.id,
          quantity: guestItem.quantity,
          start_date: guestItem.dates.start || null,
          end_date: guestItem.dates.end || null,
          kit_selections: guestItem.kitSelections ? JSON.stringify(guestItem.kitSelections) : null,
        });
      }
    }

    // Revalidate cart-related paths
    revalidatePath('/', 'layout');

    return { success: true };
  } catch (error) {
    console.error("Merge guest cart error:", error);
    return { success: false, error: "Failed to merge cart" };
  }
}

export async function resolveKit(productId: string): Promise<ResolveKitResult> {
  try {
    const pb = await createServerClient();
    const cartService = getCartService(pb);

    const kit = await cartService.resolveKit(productId);

    return { success: true, kit };
  } catch (error) {
    console.error("Resolve kit error:", error);
    return { success: false, error: "Failed to resolve kit" };
  }
}

import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import PocketBase from 'pocketbase';
import acceptLanguage from 'accept-language';
import { fallbackLng, languages } from './app/i18n/settings';
import { ROLES } from './types/auth';

acceptLanguage.languages(languages);

export const config = {
  // Matcher ignoring `/_next/`, `/api/`, `..` etc
  matcher: ['/((?!api|_next/static|_next/image|assets|favicon.ico|sw.js).*)'],
};

export async function middleware(request: NextRequest) {
  const response = NextResponse.next();
  const pb = new PocketBase(process.env.NEXT_PUBLIC_PB_URL);

  // -----------------------------------------------------------------------
  // 1. I18n Redirection Logic
  // -----------------------------------------------------------------------
  let lng;
  if (request.cookies.has('i18next')) {
    lng = acceptLanguage.get(request.cookies.get('i18next')?.value);
  }
  if (!lng) {
    lng = acceptLanguage.get(request.headers.get('Accept-Language'));
  }
  if (!lng) {
    lng = fallbackLng;
  }

  // Check if pathname already has a locale
  const pathname = request.nextUrl.pathname;
  const pathnameIsMissingLocale = languages.every(
    (l) => !pathname.startsWith(`/${l}/`) && pathname !== `/${l}`
  );

  // Redirect if missing locale
  if (pathnameIsMissingLocale) {
    return NextResponse.redirect(
      new URL(`/${lng}${pathname.startsWith('/') ? '' : '/'}${pathname}`, request.url)
    );
  }

  // -----------------------------------------------------------------------
  // -----------------------------------------------------------------------
  // 2. RBAC Strategy (Unified Security)
  // -----------------------------------------------------------------------

  // Extract path without locale for checking protected routes
  // e.g. "/en/admin" -> "/admin"
  const pathWithoutLocale = pathname.replace(/^\/[a-z]{2}/, '');

  const isAdminPath = pathWithoutLocale.startsWith('/admin') || pathWithoutLocale === '/admin';
  const isDashboardPath = pathWithoutLocale.startsWith('/dashboard') || pathWithoutLocale === '/dashboard';
  const isAccountPath = pathWithoutLocale.startsWith('/account');
  const isLoginPath = pathWithoutLocale.startsWith('/login') || pathWithoutLocale === '/login';
  const isRegisterPath = pathWithoutLocale.startsWith('/register') || pathWithoutLocale === '/register';

  // -----------------------------------------------------------------------
  // 2.1 Client Portal Feature Flag Check
  // -----------------------------------------------------------------------
  const isClientPortalEnabled = process.env.ENABLE_CLIENT_PORTAL === 'true';
  // Login is allowed (admins need it), but register/dashboard/account are blocked for clients
  const isClientOnlyPath = isRegisterPath || isDashboardPath || isAccountPath;

  // Block client-only routes when portal disabled (login stays available for admins)
  if (!isClientPortalEnabled && isClientOnlyPath && !isAdminPath) {
    console.log('[Middleware] Client portal disabled, blocking:', pathWithoutLocale);
    const currentLocale = pathname.split('/')[1] || fallbackLng;
    const url = request.nextUrl.clone();
    url.pathname = `/${currentLocale}/`;
    return NextResponse.redirect(url);
  }

  // If it's a protected path, we check auth
  if (isAdminPath || isDashboardPath || isAccountPath) {
    console.log("[Middleware] Protected path detected:", pathWithoutLocale);

    // Load auth state from cookies
    const authCookie = request.cookies.get('pb_auth');
    console.log("[Middleware] pb_auth cookie present:", !!authCookie);
    console.log("[Middleware] pb_auth cookie value (first 50 chars):", authCookie?.value?.substring(0, 50));

    if (authCookie) {
      try {
        // Parse the JSON cookie payload
        let decoded = decodeURIComponent(authCookie.value);

        // Handle legacy format: strip "pb_auth=" prefix if present
        if (decoded.startsWith("pb_auth=")) {
          decoded = decoded.substring(8); // Remove "pb_auth=" prefix
        }

        const authData = JSON.parse(decoded);
        console.log("[Middleware] Parsed authData - token present:", !!authData.token);
        console.log("[Middleware] Parsed authData - record present:", !!authData.record);

        // Load token and model directly into authStore
        pb.authStore.save(authData.token, authData.record);
        console.log("[Middleware] After save - isValid:", pb.authStore.isValid);
        console.log("[Middleware] After save - model id:", pb.authStore.model?.id);
      } catch (parseError) {
        console.error("[Middleware] Failed to parse auth cookie:", parseError);
      }
    }

    const isValid = pb.authStore.isValid;
    console.log("[Middleware] Final isValid check:", isValid);

    // Logic: Redirect if not allowed
    if (!isValid) {
      console.log("[Middleware] === NOT VALID - Redirecting to login ===");
      // Not logged in -> Go to Login (preserving locale)
      // Extract locale from current path to keep user in same language
      const currentLocale = pathname.split('/')[1] || fallbackLng;
      const url = request.nextUrl.clone();
      url.pathname = `/${currentLocale}/login`;
      url.searchParams.set('redirect', pathname); // Add redirect param
      return NextResponse.redirect(url);
    }

    console.log("[Middleware] === VALID - Allowing access ===");

    if (isAdminPath && isValid) {
      // Logged in but checking Role
      // NOTE: PocketBase 'admins' collection behaves differently than 'users' with role field.
      // If you are using a unified 'users' collection with a 'role' field:
      const userRole = pb.authStore.model?.role;

      // If you are using the separate 'admins' collection, pb.authStore.model will be an Admin model.
      // We check if it's NOT an admin (assuming 'users' collection role approach for now based on prompt context)
      // If using native PB admins, we would check `pb.authStore.model.collectionName === '_superusers'` or similar.

      // Strict Admin Check
      console.log(`[Middleware] Checking Admin Path: ${pathWithoutLocale}`);
      console.log(`[Middleware] User Role: ${userRole}, Email: ${pb.authStore.model?.email}`);

      if (userRole !== ROLES.ADMIN) {
        console.log('[Middleware] Access Denied: User is not admin.');
        // Not an admin -> Go to Home
        const currentLocale = pathname.split('/')[1] || fallbackLng;
        const url = request.nextUrl.clone();
        url.pathname = `/${currentLocale}/`;
        return NextResponse.redirect(url);
      }
    }
  }

  return response;
}

import PocketBase from 'pocketbase';
import { IAuthService } from './interface';
import { User, UserRole, ROLES } from '@/types/auth';
import { cookies } from 'next/headers'; // Essential for Server Actions

// Singleton instance to avoid creating multiple connections
export const pb = new PocketBase(process.env.NEXT_PUBLIC_PB_URL);

/**
 * @deprecated This service is DEPRECATED. Use Server Actions in `src/lib/actions/session.ts` instead.
 * This class bypasses the secure HttpOnly cookie authentication flow.
 */
export class PocketBaseAuthService implements IAuthService {
  constructor() {
    console.warn('[DEPRECATED] PocketBaseAuthService is deprecated. Use session.ts Server Actions instead.');
  }

  async login(email: string, pass: string) {
    const authData = await pb.collection('users').authWithPassword(email, pass);

    // Map PB record to our clean User type
    const user: User = {
      id: authData.record.id,
      email: authData.record.email,
      name: authData.record.name,
      avatar: authData.record.avatar,
      role: (authData.record.role as UserRole) || 'customer',
    };

    return { user, token: authData.token };
  }

  logout() {
    pb.authStore.clear();
    // If using cookies for session, we would clear them here
  }

  async getCurrentUser(): Promise<User | null> {
    // In Next.js Server Components, we often check the cookie/header
    // For client-side, we check authStore
    if (!pb.authStore.isValid) return null;

    const model = pb.authStore.model;
    if (!model) return null;

    return {
      id: model.id,
      email: model.email,
      name: model.name,
      avatar: model.avatar,
      role: (model.role as UserRole) || 'customer',
    };
  }

  isAdmin(user: User): boolean {
    return user.role === ROLES.ADMIN;
  }
}

import PocketBase from 'pocketbase';
import { Cart, CartItem, KitTemplate, KitItem, ResolvedKit, ResolvedKitSlot } from '@/types/commerce';
import { Product } from '@/services/products/types';

const PB_URL = process.env.NEXT_PUBLIC_PB_URL || 'http://127.0.0.1:8090';

export class CartService {
  private pb: PocketBase;

  constructor(pbClient: PocketBase) {
    this.pb = pbClient;
  }

  // 1. THE TRIGGER: Check if product forces a bundle
  async checkBundleRequirement(productId: string): Promise<{ hasBundle: boolean; template?: KitTemplate; items?: KitItem[] }> {
    try {
      // Find a template where this product is the "Main Trigger"
      const template = await this.pb.collection('kit_templates').getFirstListItem(`main_product="${productId}"`);

      if (!template) return { hasBundle: false };

      // Fetch the ingredients
      const items = await this.pb.collection('kit_items').getFullList({
        filter: `template="${template.id}"`,
        expand: 'product',
      });

      return {
        hasBundle: true,
        template: {
          id: template.id,
          name: template.name,
          main_product_id: template.main_product,
          base_price_modifier: template.base_price_modifier,
        },
        items: items.map(record => ({
            id: record.id,
            product_id: record.product,
            product: record.expand?.product,
            is_mandatory: record.is_mandatory,
            default_quantity: record.default_quantity,
            slot_name: record.slot_name,
            swappable_category_id: record.swappable_category
        }))
      };
    } catch (e) {
      // 404 means no bundle found, which is fine
      return { hasBundle: false };
    }
  }

  // 1b. RESOLVE KIT: Convert DB structure to UI-friendly ResolvedKit
  async resolveKit(productId: string): Promise<ResolvedKit | null> {
    const bundleCheck = await this.checkBundleRequirement(productId);
    if (!bundleCheck.hasBundle || !bundleCheck.template || !bundleCheck.items) {
      return null;
    }

    // Get the main product (PUBLIC fields only - no pricing)
    let mainProduct: Product;
    try {
      const record = await this.pb.collection('equipment').getOne(productId);
      mainProduct = {
        id: record.id,
        name: record.name_en || record.name,
        nameEn: record.name_en || record.name,
        nameFr: record.name_fr || record.name,
        slug: record.slug,
        categoryId: record.category,
        // BLIND QUOTE: No dailyRate, stockTotal, stockAvailable
        isAvailable: (record.stock_available || record.stock || 0) > 0,
        imageUrl: record.images?.[0] ? `${PB_URL}/api/files/equipment/${record.id}/${record.images[0]}` : undefined,
      };
    } catch {
      return null;
    }

    // Group items by slot_name
    const slotMap = new Map<string, KitItem[]>();
    for (const item of bundleCheck.items) {
      const slotName = item.slot_name || 'Default';
      if (!slotMap.has(slotName)) {
        slotMap.set(slotName, []);
      }
      slotMap.get(slotName)!.push(item);
    }

    // Build resolved slots
    const slots: ResolvedKitSlot[] = [];
    for (const [slotName, items] of slotMap) {
      // Get available options from swappable category
      let availableOptions: Product[] = [];
      const categoryId = items[0]?.swappable_category_id;
      if (categoryId) {
        try {
          const records = await this.pb.collection('equipment').getFullList({
            filter: `category="${categoryId}"`,
          });
          // PUBLIC Product type only - no pricing data (BLIND QUOTE)
          availableOptions = records.map(r => ({
            id: r.id,
            name: r.name_en || r.name,
            nameEn: r.name_en || r.name,
            nameFr: r.name_fr || r.name,
            slug: r.slug,
            categoryId: r.category,
            // BLIND QUOTE: No dailyRate, stockTotal, stockAvailable
            isAvailable: (r.stock_available || r.stock || 0) > 0,
            imageUrl: r.images?.[0] ? `${PB_URL}/api/files/equipment/${r.id}/${r.images[0]}` : undefined,
          }));
        } catch {
          // No available options
        }
      }

      // Check if any item in this slot is swappable (allows selection)
      const allowMultiple = items.some(i => !i.is_mandatory);
      const required = items.some(i => i.is_mandatory);

      slots.push({
        slotName,
        required,
        allowMultiple,
        defaultItems: items.filter(i => i.is_mandatory),
        selectedItems: items, // Start with all items selected
        availableOptions,
      });
    }

    return {
      template: bundleCheck.template,
      mainProduct,
      slots,
    };
  }

  // 2. THE ACTION: Add a "Smart Group" to cart
  async addBundleToCart(userId: string, selections: { productId: string; quantity: number }[], dates: { start: Date; end: Date }) {
    // A. Get or Create Cart
    const cartId = await this.getOrCreateCartId(userId);

    // B. Generate a Unique Group ID (UUID)
    const groupId = crypto.randomUUID();

    // C. Batch Create Items
    // We use a Promise.all to add them "simultaneously"
    // In a real production app, we would use a Batch Transaction here
    const promises = selections.map(item => {
      return this.pb.collection('cart_items').create({
        cart: cartId,
        product: item.productId,
        quantity: item.quantity,
        group_id: groupId, // <-- This glues them together
        dates: {
            start: dates.start.toISOString(),
            end: dates.end.toISOString()
        }
      });
    });

    await Promise.all(promises);
    return { success: true, groupId };
  }

  // Helper: Get active cart
  private async getOrCreateCartId(userId: string): Promise<string> {
    try {
      const cart = await this.pb.collection('carts').getFirstListItem(`user="${userId}" && status="active"`);
      return cart.id;
    } catch {
      const newCart = await this.pb.collection('carts').create({
        user: userId,
        status: 'active'
      });
      return newCart.id;
    }
  }

  // 3. FETCH: Get Cart with Hierarchy
  async getCart(userId: string): Promise<Cart | null> {
    try {
        const cartRecord = await this.pb.collection('carts').getFirstListItem(`user="${userId}" && status="active"`);
        const items = await this.pb.collection('cart_items').getFullList({
            filter: `cart="${cartRecord.id}"`,
            expand: 'product',
            sort: 'group_id,created' // Group items together visually
        });

        return {
            id: cartRecord.id,
            user_id: cartRecord.user,
            status: cartRecord.status,
            items: items.map(item => ({
                id: item.id,
                product: item.expand?.product,
                quantity: item.quantity,
                group_id: item.group_id,
                dates: {
                    start: new Date(item.dates.start),
                    end: new Date(item.dates.end)
                }
            })) as CartItem[]
        };
    } catch {
        return null;
    }
  }
}

import PocketBase, { RecordModel } from 'pocketbase';
import { IProductService, ProductFilters, PaginatedResult } from './interface';
import { Product, Category } from './types';

const PB_URL = process.env.NEXT_PUBLIC_PB_URL || 'http://127.0.0.1:8090';

/**
 * Map raw DB record to clean Product type
 * 
 * CATALOG MODE / BLIND QUOTE:
 * - Does NOT expose: dailyRate, stockTotal, stockAvailable
 * - Only exposes isAvailable boolean (computed from stock)
 * - Pricing and stock data never reaches the client
 * 
 * Handles TDD schema fields (name_en, name_fr, description_en, description_fr)
 * with fallback to legacy single-language fields for compatibility.
 * 
 * @param record - PocketBase record
 * @param lang - Language code (defaults to 'en')
 */
function mapRecordToProduct(record: RecordModel, lang: string = 'en'): Product {
    // Handle bilingual name (TDD: name_en, name_fr) with fallback
    const nameEn = record.name_en || record.name || ''
    const nameFr = record.name_fr || record.name || nameEn
    const name = lang === 'fr' ? nameFr : nameEn

    // Handle bilingual description (TDD: description_en, description_fr)
    const descriptionEn = record.description_en || record.description
    const descriptionFr = record.description_fr || record.description || descriptionEn
    const description = lang === 'fr' ? descriptionFr : descriptionEn

    // Handle bilingual specs (TDD: specs_en, specs_fr)
    const specsEn = record.specs_en
    const specsFr = record.specs_fr
    const specsRaw = lang === 'fr' ? (specsFr || specsEn) : (specsEn || specsFr)

    // Parse specs if it's a string
    let specs: Record<string, string | number> = {}
    if (typeof specsRaw === 'string') {
        try {
            specs = JSON.parse(specsRaw)
        } catch {
            specs = {}
        }
    } else if (typeof specsRaw === 'object' && specsRaw !== null) {
        specs = specsRaw
    } else if (record.specs) {
        specs = typeof record.specs === 'string' ? JSON.parse(record.specs) : record.specs
    }

    // Calculate availability without exposing stock numbers
    const stockAvailable = record.stock_available ?? record.stock_total ?? record.stock ?? 0
    const isAvailable = record.visibility !== false &&
        record.availability_status !== 'maintenance' &&
        stockAvailable > 0

    // Return PUBLIC Product type - NO pricing or stock numbers exposed
    return {
        id: record.id,
        // Bilingual fields
        nameEn,
        nameFr,
        name,
        slug: record.slug || record.id,
        categoryId: record.category,
        brand: record.brand,
        // Bilingual descriptions
        descriptionEn,
        descriptionFr,
        description,
        // Bilingual specs
        specsEn: record.specs_en,
        specsFr: record.specs_fr,
        specs,
        // Images - handle both array and single file fields
        imageUrl: Array.isArray(record.images) && record.images[0]
            ? `${PB_URL}/api/files/${record.collectionId}/${record.id}/${record.images[0]}`
            : record.image
                ? `${PB_URL}/api/files/${record.collectionId}/${record.id}/${record.image}`
                : undefined,
        images: Array.isArray(record.images)
            ? record.images.map((img: string) =>
                `${PB_URL}/api/files/${record.collectionId}/${record.id}/${img}`
            )
            : undefined,
        // Status flags - ONLY boolean availability, no stock numbers
        isFeatured: record.featured || record.is_featured || false,
        isAvailable,
        visibility: record.visibility !== false,
        // NOTE: dailyRate, stockTotal, stockAvailable are INTENTIONALLY OMITTED
        // They are only available via ProductInternal for admin use
    }
}

// Wrapper for use in .map() - uses default English
const mapRecordToProductDefault = (record: RecordModel): Product => mapRecordToProduct(record, 'en');

// Map raw DB record to clean Category type
const mapRecordToCategory = (record: RecordModel): Category => ({
    id: record.id,
    name: record.name,
    slug: record.slug || record.id,
    description: record.description,
    thumbnail: record.thumbnail
        ? `${PB_URL}/api/files/${record.collectionId}/${record.id}/${record.thumbnail}`
        : undefined,
});

// Collection names per TDD cinema-tdd.md
const EQUIPMENT_COLLECTION = 'products'; // Changed from 'equipment' to match PocketBase migration
const CATEGORIES_COLLECTION = 'categories';

export class PocketBaseProductService implements IProductService {
    private pb: PocketBase;

    constructor(pbClient: PocketBase) {
        this.pb = pbClient;
    }

    async getAllProducts(): Promise<Product[]> {
        try {
            const records = await this.pb.collection(EQUIPMENT_COLLECTION).getFullList({
                sort: 'name_en',
            });
            return records.map(mapRecordToProductDefault);
        } catch (error) {
            console.error('Error fetching all products:', error);
            return [];
        }
    }

    async getProductById(id: string): Promise<Product | null> {
        try {
            const record = await this.pb.collection(EQUIPMENT_COLLECTION).getOne(id, {
                expand: 'category',
            });
            const product = mapRecordToProduct(record);
            if (record.expand?.category) {
                product.category = mapRecordToCategory(record.expand.category);
            }
            return product;
        } catch (error) {
            console.error('Error fetching product by id:', error);
            return null;
        }
    }

    async getProductBySlug(slug: string, retries: number = 3): Promise<Product | null> {
        // Add retry logic for cold start / connection issues
        for (let attempt = 1; attempt <= retries; attempt++) {
            try {
                const record = await this.pb.collection(EQUIPMENT_COLLECTION).getFirstListItem(
                    `slug = "${slug}"`,
                    { expand: 'category' }
                );
                const product = mapRecordToProduct(record);
                if (record.expand?.category) {
                    product.category = mapRecordToCategory(record.expand.category);
                }
                return product;
            } catch (error: any) {
                const isNotFound = error?.status === 404 || error?.message?.includes('not found');

                // If it's genuinely not found (not a connection error), don't retry
                if (isNotFound) {
                    console.log(`Product with slug "${slug}" not found in database`);
                    return null;
                }

                // Log the error with attempt number
                console.error(`Error fetching product by slug (attempt ${attempt}/${retries}):`, error);

                // Wait before retrying (exponential backoff)
                if (attempt < retries) {
                    await new Promise(resolve => setTimeout(resolve, attempt * 200));
                }
            }
        }

        // All retries exhausted
        console.error(`Failed to fetch product with slug "${slug}" after ${retries} attempts`);
        return null;
    }

    async getFeaturedProducts(): Promise<Product[]> {
        try {
            const records = await this.pb.collection(EQUIPMENT_COLLECTION).getList(1, 6, {
                filter: 'featured = true',
                sort: 'name_en',
            });
            return records.items.map(mapRecordToProductDefault);
        } catch (error) {
            console.error('Error fetching featured products:', error);
            return [];
        }
    }

    async getProducts(
        filters?: ProductFilters,
        page: number = 1,
        perPage: number = 12
    ): Promise<PaginatedResult<Product>> {
        try {
            // Build filter string
            const filterParts: string[] = [];

            if (filters?.categorySlug) {
                try {
                    const category = await this.pb.collection(CATEGORIES_COLLECTION).getFirstListItem(`slug="${filters.categorySlug}"`);
                    filterParts.push(`category = "${category.id}"`);
                } catch (e) {
                    return { items: [], totalItems: 0, page, totalPages: 0, perPage };
                }
            }

            if (filters?.search) {
                const searchTerm = filters.search.replace(/"/g, '\\"');
                // Use actual fields from products collection schema
                filterParts.push(`(name ~ "${searchTerm}" || description ~ "${searchTerm}")`);
            }

            if (filters?.minPrice !== undefined) {
                filterParts.push(`(daily_rate >= ${filters.minPrice})`);
            }

            if (filters?.maxPrice !== undefined) {
                filterParts.push(`(daily_rate <= ${filters.maxPrice})`);
            }

            if (filters?.isAvailable) {
                filterParts.push('(stock_available > 0)');
            }

            const filterString = filterParts.length > 0 ? filterParts.join(' && ') : '';

            const result = await this.pb.collection(EQUIPMENT_COLLECTION).getList(page, perPage, {
                filter: filterString || undefined,
                sort: 'name', // Changed from 'name_en' to match products collection schema
                expand: 'category',
            });

            return {
                items: result.items.map((record) => {
                    const product = mapRecordToProduct(record);
                    if (record.expand?.category) {
                        product.category = mapRecordToCategory(record.expand.category);
                    }
                    return product;
                }),
                page: result.page,
                perPage: result.perPage,
                totalItems: result.totalItems,
                totalPages: result.totalPages,
            };
        } catch (error) {
            console.error('Error fetching products:', error);
            // Return empty result on error (e.g., collection doesn't exist)
            return {
                items: [],
                page: 1,
                perPage,
                totalItems: 0,
                totalPages: 0,
            };
        }
    }

    async getCategories(): Promise<Category[]> {
        try {
            const records = await this.pb.collection(CATEGORIES_COLLECTION).getFullList({
                sort: 'name',
            });
            return records.map(mapRecordToCategory);
        } catch (error) {
            console.error('Error fetching categories:', error);
            // Return empty array on error (e.g., collection doesn't exist)
            return [];
        }
    }

    async getCategoryBySlug(slug: string): Promise<Category | null> {
        try {
            const record = await this.pb.collection(CATEGORIES_COLLECTION).getFirstListItem(
                `slug = "${slug}"`
            );
            return mapRecordToCategory(record);
        } catch {
            return null;
        }
    }
}

/**
 * PocketBase Quote Service
 * 
 * Implements IQuoteService using PocketBase 'quotes' collection.
 * Follows TDD schema from cinema-tdd.md:
 * - client_name, client_email, client_phone, client_company
 * - items_json, rental_start_date, rental_end_date
 * - project_description, location, special_requests
 * - status, internal_notes, estimated_price, pdf_generated, follow_up_date
 */

import PocketBase from 'pocketbase'
import type {
  IQuoteService,
  CreateQuotePayload,
  Quote,
  QuoteResult,
  QuoteStatus,
} from './interface'
import type { PaginatedResult } from '@/services/products/interface'

const POCKETBASE_URL = process.env.NEXT_PUBLIC_POCKETBASE_URL || 'http://127.0.0.1:8090'

export class PocketBaseQuoteService implements IQuoteService {
  private pb: PocketBase

  constructor(pbClient: PocketBase) {
    if (!pbClient) {
      throw new Error('[PocketBaseQuoteService] An authenticated PocketBase client is required.');
    }
    this.pb = pbClient
  }

  /**
   * Map PocketBase record to domain Quote type
   */
  private mapRecordToQuote(record: Record<string, unknown>): Quote {
    return {
      id: record.id as string,
      clientName: record.client_name as string,
      clientEmail: record.client_email as string,
      clientPhone: record.client_phone as string,
      clientCompany: record.client_company as string | undefined,
      // Safely handle items_json which might be object (PB auto-parse) or string
      itemsJson: typeof record.items_json === 'string'
        ? record.items_json
        : JSON.stringify(record.items_json || []),
      rentalStartDate: record.rental_start_date as string,
      rentalEndDate: record.rental_end_date as string,
      projectDescription: record.project_description as string | undefined,
      specialRequests: record.special_requests as string | undefined,
      status: (record.status as QuoteStatus) || 'pending',
      internalNotes: record.internal_notes as string | undefined,
      estimatedPrice: record.estimated_price as number | undefined,
      pdfGenerated: (record.pdf_generated as boolean) || false,
      // Map attached quote_pdf file field - PocketBase stores filename, we need to build full URL
      pdfFileUrl: record.quote_pdf
        ? `${process.env.NEXT_PUBLIC_POCKETBASE_URL || 'http://127.0.0.1:8090'}/api/files/quotes/${record.id}/${record.quote_pdf}`
        : undefined,
      pdfFileName: record.quote_pdf as string | undefined,
      followUpDate: record.follow_up_date as string | undefined,
      quotedAt: record.quoted_at as string | undefined,  // Timestamp when quote was sent
      created: record.created as string,
      updated: record.updated as string,
      expand: record.expand as any,
      isLocked: record.locked as boolean,
    }
  }

  /**
   * Generate a human-readable confirmation number
   */
  private generateConfirmationNumber(): string {
    const now = new Date()
    const year = now.getFullYear().toString().slice(-2)
    const month = (now.getMonth() + 1).toString().padStart(2, '0')
    const day = now.getDate().toString().padStart(2, '0')
    const chars = 'ABCDEFGHJKLMNPQRSTUVWXYZ23456789'
    let suffix = ''
    for (let i = 0; i < 4; i++) {
      suffix += chars[Math.floor(Math.random() * chars.length)]
    }
    return `TFS-${year}${month}${day}-${suffix}`
  }

  async createQuote(payload: CreateQuotePayload): Promise<QuoteResult> {
    try {
      const confirmationNumber = this.generateConfirmationNumber()
      const accessToken = crypto.randomUUID()

      // Build the record data - only include user if provided
      const recordData: Record<string, unknown> = {
        client_name: payload.clientName,
        client_email: payload.clientEmail,
        client_phone: payload.clientPhone,
        client_company: payload.clientCompany || '',
        items_json: payload.items, // PocketBase json field expects the actual object
        rental_start_date: new Date(payload.rentalStartDate).toISOString().split('T')[0],
        rental_end_date: new Date(payload.rentalEndDate).toISOString().split('T')[0],
        project_description: payload.projectDescription || '',
        special_requests: payload.specialRequests || '',
        status: 'pending' as QuoteStatus,
        // pdf_generated: false, // Let database use default (false)
        confirmation_number: confirmationNumber,
        language: payload.language || 'en',
        access_token: accessToken, // Added via migration
      }

      // Only add user relation if provided (don't send empty string)
      if (payload.userId) {
        recordData.user = payload.userId
      }

      console.log('[QuoteService] Creating quote with payload:', JSON.stringify(recordData, null, 2))

      const record = await this.pb.collection('quotes').create(recordData)

      // Log the magic link for testing (temporary)
      console.log(`≡ƒÄ¼ Magic Link Generated: /en/quote/${record.id}?token=${accessToken}`)

      return { success: true, data: { quoteId: record.id, confirmationNumber, accessToken } }
    } catch (error: any) {
      console.error('Quote creation error:', error)
      // Log detailed PocketBase error response if available
      if (error?.response?.data) {
        console.error('PocketBase error details:', JSON.stringify(error.response.data, null, 2))
      }
      if (error?.response) {
        console.error('PocketBase full response:', JSON.stringify(error.response, null, 2))
      }
      return { success: false, error: 'Unable to create quote. Please try again.' }
    }
  }

  async getUserQuotes(userId: string): Promise<Quote[]> {
    try {
      const records = await this.pb.collection('quotes').getList(1, 50, {
        filter: `user = "${userId}"`,
        sort: '-created',
      })
      return records.items.map((r) => this.mapRecordToQuote(r))
    } catch (error) {
      console.error('Error fetching user quotes:', error)
      return []
    }
  }

  async getQuotesByEmail(email: string): Promise<Quote[]> {
    try {
      const records = await this.pb.collection('quotes').getList(1, 50, {
        filter: `client_email = "${email}"`,
        sort: '-created',
      })
      return records.items.map((r) => this.mapRecordToQuote(r))
    } catch (error) {
      console.error('Error fetching quotes by email:', error)
      return []
    }
  }

  async getQuoteById(quoteId: string): Promise<Quote | null> {
    try {
      const record = await this.pb.collection('quotes').getOne(quoteId, { expand: 'user' })
      return this.mapRecordToQuote(record)
    } catch (error) {
      console.error('Error fetching quote:', error)
      return null
    }
  }

  // ... (existing methods)

  async getQuotes(page = 1, perPage = 20, status?: QuoteStatus): Promise<PaginatedResult<Quote>> {
    try {
      const filterString = status ? `status = "${status}"` : ''
      const options: any = {
        // sort: '-created', // TEMP: Disable to see if this is the cause
        expand: 'user',
      }
      // ONLY add filter if it is not empty string, otherwise API throws 400
      if (filterString) {
        options.filter = filterString
      }

      console.log('[PocketBaseQuoteService] Fetching quotes. Options:', JSON.stringify(options))

      const records = await this.pb.collection('quotes').getList(page, perPage, options)

      console.log('[PocketBaseQuoteService] Success. Total items:', records.totalItems)

      return {
        items: records.items.map((r) => this.mapRecordToQuote(r)),
        page: records.page,
        perPage: records.perPage,
        totalItems: records.totalItems,
        totalPages: records.totalPages,
      }
    } catch (error) {
      console.error('[PocketBaseQuoteService] Error fetching admin quotes:', error)
      return {
        items: [],
        page,
        perPage,
        totalItems: 0,
        totalPages: 0,
      }
    }
  }

  async updateQuoteStatus(
    quoteId: string,
    status: QuoteStatus,
    internalNotes?: string
  ): Promise<Quote> {
    const updateData: Record<string, unknown> = { status }
    if (internalNotes !== undefined) {
      updateData.internal_notes = internalNotes
    }

    const record = await this.pb.collection('quotes').update(quoteId, updateData)
    return this.mapRecordToQuote(record)
  }

  async setEstimatedPrice(quoteId: string, price: number): Promise<Quote> {
    const record = await this.pb.collection('quotes').update(quoteId, {
      estimated_price: price,
    })
    return this.mapRecordToQuote(record)
  }
  async uploadQuote(id: string, file: File, price: number): Promise<QuoteResult> {
    try {
      const formData = new FormData()
      formData.append('quote_pdf', file)
      formData.append('estimated_price', price.toString())
      formData.append('status', 'quoted')
      formData.append('locked', 'false')  // Changed: Allow editing during grace period
      formData.append('quoted_at', new Date().toISOString())  // Set timestamp for grace period

      const record = await this.pb.collection('quotes').update(id, formData)
      return { success: true, data: this.mapRecordToQuote(record) }
    } catch (error) {
      console.error('Error uploading quote:', error)
      return { success: false, error: 'Failed to upload quote and lock record.' }
    }
  }
}
